INSERT INTO `problems` VALUES ('79', '将总体按照一定标准分成若干个层次或类别，然后根据事先确定的样本容量和各层、各类在总体中所占的比例，分别随机抽取一定数目的样本的抽样方法称为（  ）', 'A. 分层随机抽样 \nB. 整群随机抽样 \nC. 等距抽样 \nD. 单纯随机抽样 \n', '正确答案是：分层随机抽样');
INSERT INTO `problems` VALUES ('80', '预测研究在于分析事物未来发展的前景和趋势，回答的问题是（    ）问题。', 'A. 为什么 \nB. 是什么 \nC. 怎么样 \nD. 将会怎么样 \n', '正确答案是：将会怎么样');
INSERT INTO `problems` VALUES ('81', '以（  ）为标准进行分类，可以将教育研究方法分为量化研究方法、质化研究方法、混合研究方法。', 'A. 数据分析 \nB. 研究过程的阶段 \nC. 问题性质 \nD. 研究场所 \n', '正确答案是：数据分析');
INSERT INTO `problems` VALUES ('82', '如下是钱昆的毕业论文中参考文献的书写格式，其中正确的是（  ）', 'A. 何丽．深度学习的困境与超越[N]．全球教育展望，2009（6）：15． \nB. 徐洪镇．微课教学之我见[J]．教师月刊，2012（11）：16． \nC. 刘春生．作业的革命[D]．世界图书出版公司，2007：8-9． \nD. 方宏．教育哲学[M]．华东师范大学出版社，2012：56-58． \n', '正确答案是：徐洪镇．微课教学之我见[J]．教师月刊，2012（11）：16．');
INSERT INTO `problems` VALUES ('83', '抽样的基本要求不包括（ ）', 'A. 明确总体范围 \nB. 样本的容量越大越好 \nC. 样本要具有代表性 \nD. 抽样要有随机性 \n', '正确答案是：样本的容量越大越好');
INSERT INTO `problems` VALUES ('84', '（  ）相对于其他量表具有绝对零点。', 'A. 等比量表 \nB. 等距量表 \nC. 类别量表 \nD. 顺序量表 \n', '正确答案是：等比量表');
INSERT INTO `problems` VALUES ('85', '下列不属于访谈调查研究优点的是（      ）。', 'A. 直接性  \nB. 调查的范围广 \nC. 灵活性强 \nD. 不受语言文字限制 \n', '正确答案是：调查的范围广');
INSERT INTO `problems` VALUES ('86', '教育行动研究的主要目的是（  ）。', 'A. 解决实践中存在的问题 \nB. 促进学生的发展 \nC. 将教师培养成为研究者 \nD. 形成理论 \n', '正确答案是：解决实践中存在的问题');
INSERT INTO `problems` VALUES ('87', '在访谈中，访谈人员具有适当解说、引导和追问的机会，从而可探讨较复杂的问题，获取深层次信息，这体现出访谈调查的（  ）。', 'A. 深入性 \nB. 客观性 \nC. 真实性 \nD. 准确性 \n', '正确答案是：深入性');
INSERT INTO `problems` VALUES ('88', '下列说法中不正确的是（  ）', 'A. 个案研究是一种历时性的研究 \nB. 个案研究的目的是多样的 \nC. 个案研究又称个案追踪法 \nD. 个案研究的“个案”指的是个人 \n', '正确答案是：个案研究的“个案”指的是个人');
INSERT INTO `problems` VALUES ('89', '下列选项中不属于个案研究之局限性的是（  ）', 'A. 主观性较强  \nB. 结论缺乏普遍性 \nC. 难以重复验证 \nD. 结果更具有针对性 \n', '正确答案是：结果更具有针对性');
INSERT INTO `problems` VALUES ('90', '下列关于于个案研究评价的说法，正确的是（  ）。', 'A. 调查方法的单一性 \nB. 研究资料的确凿性 \nC. 研究视角的主观性 \nD. 研究报告的格式的统一性 \n', '正确答案是：研究资料的确凿性');
INSERT INTO `problems` VALUES ('91', '下列关于个案研究的说法，错误的是（  ）', 'A. 个案研究追求复杂性 \nB. 研究对象具有单一性 \nC. 研究目的是可以描述的 \nD. 个案研究又称个案追踪法 \n', '正确答案是：个案研究追求复杂性');
INSERT INTO `problems` VALUES ('92', '个案研究的优势不包括（  ）。', 'A. 结果更具有针对性 \nB. 更易得出普遍性结论 \nC. 为特殊事件研究所必需 \nD. 过程体现生态化 \n', '正确答案是：更易得出普遍性结论');
INSERT INTO `problems` VALUES ('93', '有教师对学生的主题班会从头至尾地进行观察，注重特定行为的特征，关心的是行为如何发生、如何变化、结果如何等问题。此种观察运用了哪种观察方式（  ）。', 'A. 行为抽样 \nB. 学生抽样 \nC. 时间抽样 \nD. 事件抽样 \n', '正确答案是：事件抽样');
INSERT INTO `problems` VALUES ('94', '把认真听讲的学生用“↑”表示，不认真听讲的学生用“↓”表示，这种记录法属于（  ）', 'A. 描述记录法 \nB. 标记记录法 \nC. 等级记录法 \nD. 日记描述法 \n', '正确答案是：标记记录法');
INSERT INTO `problems` VALUES ('95', '下列描述中，哪种说法符合教育观察的定义（ ）。', 'A. 教育观察总是在实验室进行的观察 \nB. 教育观察总要借助一定的工具 \nC. 教育观察是无意识、无目的的观察 \nD. 教育观察是对过去发生的事的考察 \n', '正确答案是：教育观察总要借助一定的工具');
INSERT INTO `problems` VALUES ('96', '将教育观察划分为结构式观察与非结构式观察，其划分的标准是（  ）。', 'A. 观察者是否直接介入观察对象的活动 \nB. 观察情境的性质 \nC. 观察时是否借助有关仪器设备 \nD. 观察内容是否有统一设计、明确具体的观察要求 \n', '正确答案是：观察内容是否有统一设计、明确具体的观察要求');
INSERT INTO `problems` VALUES ('97', '教育行动研究中，研究题目就是研究问题的摘要叙述，如针对要改进的问题，可用（ ）方式来陈述。', 'A. “解决……”、“分析……” \nB. “提升……”、“改进……” \nC. “……设计”、“……建构” \nD. “……发展”、“改进……” \n', '正确答案是：“提升……”、“改进……”');
INSERT INTO `problems` VALUES ('98', '第一个明确提出“教师成为研究者”的学者是（  ）。', 'A. 勒温 \nB. 麦克尔南 \nC. 白金汉姆 \nD. 斯腾豪斯 \n', '正确答案是：斯腾豪斯');
INSERT INTO `problems` VALUES ('99', '在学校的自然情境中，由学校教师与校外专家共同合作，针对实际问题提出改进计划，经由实施、反思和修改而得到研究结果的研究方法就是（  ）', 'A. 教育实验研究 \nB. 经验总结法 \nC. 教育行动研究 \nD. 个案研究法 \n', '正确答案是：教育行动研究');
INSERT INTO `problems` VALUES ('100', '教育行动研究的具体实施涉及各个阶段的工作落实，包括（  ）。', 'A. 从研究缘起到研究报告的撰写 \nB. 从研究计划到研究反思 \nC. 从问题的提出到问题的解决 \nD. 从研究目的与问题的起始到评价问题 \n', '正确答案是：从研究缘起到研究报告的撰写');
INSERT INTO `problems` VALUES ('101', '关于教育行动研究的协同合作，如下哪项不是中小学教师开展的合作（  ）。', 'A. 研究机构的专家与高校教师之间的合作 \nB. 一线教师之间的合作 \nC. 一线教师与高校教师之间的合作  \nD. 研究机构的专家与一线教师之间的合作 \n', '正确答案是：研究机构的专家与高校教师之间的合作');
INSERT INTO `problems` VALUES ('102', '教育文献综述的核心是 (  ) 。', 'A. 总结 \nB. 主体 \nC. 前言 \nD. 参考文献 \n', '正确答案是：主体');
INSERT INTO `problems` VALUES ('103', '下列不属于假设的特征的是（   ）。', 'A. 具有多样性 \nB. 具有确定性 \nC. 具有一定的科学依据 \nD. 具有一定的猜测性 \n', '正确答案是：具有确定性');
INSERT INTO `problems` VALUES ('104', '教育实验研究必须证明的是变量之间的（ ）', 'A. 必然的因果关系 \nB. 从属的关系 \nC. 直接或间接的关系 \nD. 相关性关系 \n', '正确答案是：必然的因果关系');
INSERT INTO `problems` VALUES ('105', '一位小学语文教师深感小学生作文言之无物，于是设计“情景作文”方法在班上试行，经过一段时间后发现作文质量有较普遍的提高。在这个实验中，其设计特点是（  ）', 'A. 单组仅后测设计 \nB. 真实验设计 \nC. 单组前后测设计 \nD. 因素设计  \n', '正确答案是：单组仅后测设计');
INSERT INTO `problems` VALUES ('106', '最早提出“实验教育学”名称的是（  ）。', 'A. 赖依 \nB. 裴斯泰洛齐 \nC. 梅依曼 \nD. 杜威 \n', '正确答案是：梅依曼');
INSERT INTO `problems` VALUES ('107', '影响实验内在效度的因素是（   ）。', 'A. 测量工具的局限 \nB. 被试样本缺乏代表性 \nC. 实验情境的人为性 \nD. 发展与成熟 \n', '正确答案是：发展与成熟');
INSERT INTO `problems` VALUES ('108', '研究结果能被推广到其他总体条件、时间和背景中的程度属于（  ）', 'A. 内在效度 \nB. 内在信度 \nC. 外在信度 \nD. 外在效度 \n', '正确答案是：外在效度');
INSERT INTO `problems` VALUES ('109', '反应测量所得结果的可靠性和稳定性程度的指标是（   ）', 'A. 效度 \nB. 难度 \nC. 信度 \nD. 区分度 \n', '正确答案是：信度');
INSERT INTO `problems` VALUES ('110', '在“合作学习对高中生数学学习成绩的影响”的实验研究中，无关变量是（    ）', 'A. 合作学习 \nB. 高中生数学学习成绩  \nC. 教师的教学能力 \nD. 高中生的学习方式 \n', '正确答案是：教师的教学能力');
INSERT INTO `problems` VALUES ('111', '在“混合式教学影响初中生学习”的陈述中，其中因变量是（  ）', 'A. 混合式教学和初中生学习都不对 \nB. 混合式教学 \nC. 混合式教学和初中生学习 \nD. 初中生学习 \n', '正确答案是：初中生学习');
INSERT INTO `problems` VALUES ('112', '为了验证小组合作学习对学生学习效果的影响，某教师在自己所教的两个平行班进行了准实验研究，在该准实验研究中，其自变量是（    ）', 'A. 两个平行班 \nB. 学生学习效果 \nC. 某教师 \nD. 小组合作学习 \n', '正确答案是：小组合作学习');
INSERT INTO `problems` VALUES ('113', '“核心素养”是这一时期新出现的教育研究领域。欲对此开展研究，比较适合的资料检索方法是（  ）。', 'A. 逆查法 \nB. 引文查找法 \nC. 顺查法 \nD. 综合查找法 \n', '正确答案是：逆查法');
INSERT INTO `problems` VALUES ('114', '人类在各种社会实践中直接形成的，具有保存价值的原始文献是（  ）。', 'A. 工具书 \nB. 数据库 \nC. 教育法令集 \nD. 报纸 \n', '正确答案是：教育法令集');
INSERT INTO `problems` VALUES ('115', '对研究对象在一个较长时间内的特征进行调查，以找出其前后的变化与差异的是（  ）。 ', 'A. 现状调查 \nB. 发展调查 \nC. 相关调查 \nD. 普遍调查  \n', '正确答案是：发展调查');
INSERT INTO `problems` VALUES ('116', '下列不属于教育叙事的理论叙事观的是（  ）', 'A. 叙事重在揭示事件或故事的有意义性 \nB. 叙事的根本目标在于获得一种对教育现象的解释 \nC. 叙事就是对有意义的教育教学事件的描述 \nD. 叙事更多地偏向“故事”而非“研究” \n', '正确答案是：叙事更多地偏向“故事”而非“研究”');
INSERT INTO `problems` VALUES ('117', '下列关于教育叙事研究的选项，错误的是（ ）。', 'A. 教育叙事研究的事例可以进行适度虚构 \nB. 教育叙事研究是教师专业成长的一种有效的方法 \nC. 教育叙事研究有助于教师形成个人的教育理论 \nD. 教育叙事研究是对教师个人的教学实践的经验总结和反思 \n', '正确答案是：教育叙事研究的事例可以进行适度虚构');
INSERT INTO `problems` VALUES ('118', '下列选项中，不属于教育叙事研究特点的是（     ）', 'A. 教育叙事就是教师的工作日志 \nB. 具有一定的情节性 \nC. 以故事为主线，以讲述为主导 \nD. 关注教育事件中的具体人物 \n', '正确答案是：教育叙事就是教师的工作日志');
INSERT INTO `problems` VALUES ('119', '在下列选项中，哪项不是成为教育研究对象的教育问题的基本特点。（  ）', 'A. 简洁性 \nB. 未知性 \nC. 问题性 \nD. 预见性 \n', '正确答案是：简洁性');
INSERT INTO `problems` VALUES ('120', '只进行描述的研究方法是（  ）', 'A. 文献研究法 \nB. 定性研究方法 \nC. 定量研究方法 \nD. 实验研究法 \n', '正确答案是：定性研究方法');
INSERT INTO `problems` VALUES ('121', '下列不属于本科生学位论文选题方式的是（  ）。', 'A. 学生根据一定特殊情况确定选题 \nB. 教师根据一定原则拟定一些课题或方向供学生选择 \nC. 学生根据自己的兴趣爱好选择课题 \nD. 学生将课本上的题目作为自己的课题 \n', '正确答案是：学生将课本上的题目作为自己的课题');
INSERT INTO `problems` VALUES ('122', '教育研究活动的起始环节是（  ）。', 'A. 提出假设 \nB. 选择课题 \nC. 制定方案 \nD. 收集资料 \n', '正确答案是：选择课题');
INSERT INTO `problems` VALUES ('123', '理论研究的成果主要表现为（  ）。', 'A. 经验总结  \nB. 调查报告 \nC. 实验报告 \nD. 学术论文 \n', '正确答案是：学术论文');
INSERT INTO `problems` VALUES ('124', '”男生与女生在逻辑推理能力上无差异“，从性质上看，该研究假设属于（     ）。', 'A. 特定假设 \nB. 非定向假设 \nC. 虚无假设 \nD. 一般假设 \n', '正确答案是：虚无假设');
INSERT INTO `problems` VALUES ('125', '如下关于课例研究与课堂观察共性的描述，不正确的是（  ）', 'A. 都旨在提高教师的课堂教学能力 \nB. 都旨在改进课堂教学  \nC. 都需要借助有关辅助工具收集数据 \nD. 都是对课堂的现场研究 \n', '正确答案是：都是对课堂的现场研究');
INSERT INTO `problems` VALUES ('126', '提出“互动过程分析”理论，并开发了人际互动的12类行为编码的作者是（ ）。', 'A. 布罗菲 \nB. 弗兰德斯 \nC. 贝尔思 \nD. 古德 \n', '正确答案是：贝尔思');
INSERT INTO `problems` VALUES ('127', '设计了包括动机、管理、教学和期望四维课堂观察结构框架的是（    ）。', 'A. 弗兰德斯 \nB. 贝尔斯 \nC. 崔允漷 \nD. 古德、布罗菲 \n', '正确答案是：古德、布罗菲');
INSERT INTO `problems` VALUES ('128', '教师进行课堂观察后，往往要反馈观察结论，这种观察结论除了成功之处、上课教师的个人特色以外，还要反馈（  ）。', 'A. 上课内容 \nB. 存在的问题 \nC. 心得体会 \nD. 教学流程 \n', '正确答案是：存在的问题');
INSERT INTO `problems` VALUES ('129', '小张对收集到的语文教师教学信念的相关研究进行文献分析，主要采用的文献分析方法是(   )。 ', 'A. 访谈分析 \nB. 定量分析 \nC. 定性分析 \nD. 问卷分析 \n', '正确答案是：定性分析');
INSERT INTO `problems` VALUES ('130', '下列说法中错误的是（ ）。', 'A. 文献综述应当指出自身研究与已有研究的关系  \nB. 文献综述应当肯定已有的研究成果 \nC. 文献综述应当避免个人观点的表述 \nD. 文献综述应当点出现有研究的不足 \n', '正确答案是：文献综述应当避免个人观点的表述');
INSERT INTO `problems` VALUES ('131', '控制无关变量的等组实验中，使无关变量以同一水平作用于实验班和控制班的方法是（ ）。', 'A. 随机法 \nB. 平衡法 \nC. 消除法 \nD. 恒定法 \n', '正确答案是：平衡法');
INSERT INTO `problems` VALUES ('132', '在教育实验设计中，不能随机选择和安排被试，也不能对无关变量进行控制，只能操控实验变量的实验是（   ）。', 'A. 探索性实验 \nB. 真实验 \nC. 前实验 \nD. 准实验 \n', '正确答案是：前实验');
INSERT INTO `problems` VALUES ('133', '为了提高回答率，研究者不能采取的措施是（  ）。', 'A. 设计一张富有吸引力、简洁的问卷 \nB. 提供适时的跟踪发送信函 \nC. 用金钱作为报酬 \nD. 可以不择手段 \n', '正确答案是：可以不择手段');
INSERT INTO `problems` VALUES ('134', '一份问卷应保证在15分钟内完成，最好不超过（  ）。', 'A.  25分钟 \nB. 20分钟 \nC. 30分钟 \nD. 4分钟 \n', '正确答案是：20分钟');
INSERT INTO `problems` VALUES ('135', '（  ）不属于问卷调查研究的特点。', 'A. 高效性 \nB. 多样性 \nC. 统一性 \nD. 定量性 \n', '正确答案是：多样性');
INSERT INTO `problems` VALUES ('136', '关于问卷调查，下列选项中错误的是（ ）。', 'A. 问卷调查具有高效性的特点 \nB. 问卷调查具有客观性的特点 \nC. 问卷调查具有定量和定性相结合的特点 \nD. 问卷调查具有统一性的特点 \n', '正确答案是：问卷调查具有定量和定性相结合的特点');
INSERT INTO `problems` VALUES ('137', '一般来说，有效问卷回收率达到（  ）以上，问卷资料才能认为可用。', 'A. 90% \nB. 60% \nC. 80% \nD. 70% \n', '正确答案是：70%');
INSERT INTO `problems` VALUES ('138', '下列不属于教育叙事研究形式的是（   ）。', 'A. 实验的叙事研究 \nB. 记人的叙事研究 \nC. 记活动的叙事研究 \nD. 记课的叙事研究 \n', '正确答案是：实验的叙事研究');
INSERT INTO `problems` VALUES ('139', '在表述研究结果时，直接写上被研究者的姓名而没有进行匿名处理，该做法违背了（   ）', 'A. 研究伦理 \nB. 研究的客观性 \nC. 研究者的意愿 \nD. 研究的创新性 \n', '正确答案是：研究伦理');
INSERT INTO `problems` VALUES ('140', '以唯物主义基本原理为指南，以科学实践反复证实的客观规律为基础的选题原则是（  ）。', 'A. 创新性原则 \nB. 需要性原则 \nC. 科学性原则 \nD. 可行性原则 \n', '正确答案是：科学性原则');
INSERT INTO `problems` VALUES ('141', '下列说法中正确的是（ ）。', 'A. 问卷调查具有高效性的特点 \nB. 问卷调查具有定量和定性相结合的特点 \nC. 研究方法是由研究过程决定的 \nD. 观察法优于访谈法  \n', '正确答案是：问卷调查具有高效性的特点');
INSERT INTO `problems` VALUES ('142', '访谈调查的特点包括（  ）。', 'A. 灵活性 \nB. 定量性 \nC. 深入性 \nD. 统一性 \n', '正确答案是：灵活性, 深入性');
INSERT INTO `problems` VALUES ('143', '下列关于个案研究的说法，正确的是（ ）。', 'A. 个案研究的过程是深入的  \nB. 个案研究对象是单一的 \nC. 个案研究的结果更具针对性 \nD. 个案研究的手段是单一的  \n', '正确答案是：个案研究对象是单一的, 个案研究的过程是深入的 , 个案研究的结果更具针对性');
INSERT INTO `problems` VALUES ('144', '在研究“自主探究教学方法对学生学习成绩的影响”的实验中，属于无关变量的是（  ）。', 'A. 教师的教学能力 \nB. 教材内容 \nC. 学生的智力水平 \nD. 教学方法 \n', '正确答案是：教材内容, 教师的教学能力, 学生的智力水平');
INSERT INTO `problems` VALUES ('145', '遵循选题的可行性原则，选题应注意（    ）。', 'A. 前期基础扎实 \nB. 具有时代感 \nC. 保证研究时间 \nD. 研究资料充足 \n', '正确答案是：研究资料充足, 具有时代感, 保证研究时间, 前期基础扎实');
INSERT INTO `problems` VALUES ('146', '观察记录方式主要有（ ）。', 'A. 标记记录 \nB. 时间取样 \nC. 描述记录 \nD. 等级记录 \n', '正确答案是：描述记录, 标记记录, 等级记录');
INSERT INTO `problems` VALUES ('147', '观察法的设计主要包括的内容有（ ）。', 'A. 选择观察的对象 \nB. 确定观察的时间或事件，明确观察项目的精确定义 \nC. 确定观察的基本方法 \nD. 确定观察的目的 \n', '正确答案是：确定观察的目的, 确定观察的基本方法, 选择观察的对象, 确定观察的时间或事件，明确观察项目的精确定义');
INSERT INTO `problems` VALUES ('148', '关于教育行动研究的表述正确的是（  ）。', 'A. 教育行动研究是为构建理论而采取的研究 \nB. 行动研究者对自身的实践进行批判性思考 \nC. 教育行动研究者用科学的方法对自己的行动所进行的研究 \nD. 教育行动研究是由教师针对实际问题进行的研究 \n', '正确答案是：教育行动研究者用科学的方法对自己的行动所进行的研究, 教育行动研究是由教师针对实际问题进行的研究, 行动研究者对自身的实践进行批判性思考');
INSERT INTO `problems` VALUES ('149', '教育行动研究的具体实施涉及各个阶段，实施过程中应注意（  ）。', 'A. 教育研究过程必须保持充分的弹性 \nB. 由实际从事教育工作的人来进行研究 \nC. 教育行动研究者不必摒弃主观成见，可以形成自己的态度 \nD. 在实际情境中进行研究 \n', '正确答案是：在实际情境中进行研究, 教育研究过程必须保持充分的弹性, 由实际从事教育工作的人来进行研究');
INSERT INTO `problems` VALUES ('150', '以下不属于教育实验内在效度影响因素的是（   ）', 'A. 测量工具的局限性   \nB. 历史或经历 \nC. 实验情境的人为性  \nD. 发展与成熟  \n', '正确答案是：实验情境的人为性 , 测量工具的局限性  ');
INSERT INTO `problems` VALUES ('151', '文献检索方式包括（  ）。', 'A. 分类检索 \nB. 主题检索 \nC.  作者检索 \nD. 代码检索 \n', '正确答案是： 作者检索, 分类检索, 主题检索, 代码检索');
INSERT INTO `problems` VALUES ('152', '教育研究方法演进的主要趋势表现为（   ）。', 'A. 教育研究方法的混合性 \nB. 教育研究方法的多元化 \nC. 教育研究方法的移植性 \nD. 教育研究方法的技术性 \n', '正确答案是：教育研究方法的多元化, 教育研究方法的混合性, 教育研究方法的移植性, 教育研究方法的技术性');
INSERT INTO `problems` VALUES ('153', '下列属于成为教育研究对象的教育问题的基本特点的是（ ）。', 'A. 预见性 \nB. 问题性 \nC. 未知性 \nD. 简单性 \n', '正确答案是：问题性, 预见性, 未知性');
INSERT INTO `problems` VALUES ('154', '学术论文写作中，前言一般包括以下内容(   )。 ', 'A. 说明作者论证的方法和手段 \nB. 交代研究背景 \nC. 提出课题 \nD. 交代研究结果 \n', '正确答案是：提出课题, 交代研究背景, 说明作者论证的方法和手段');
INSERT INTO `problems` VALUES ('155', '准实验设计的特点是（  ）。', 'A. 被试不是随机的 \nB. 不控制无关变量 \nC. 能操控实验变量 \nD. 尽可能控制无关变量 \n', '正确答案是：被试不是随机的, 尽可能控制无关变量, 能操控实验变量');
INSERT INTO `problems` VALUES ('156', '下列说法中不正确的是（ ）。', 'A. 问卷调查具有高效性的特点 \nB. 问卷调查具有定量和定性相结合的特点 \nC. 问卷调查过程中发放的问卷越多越好   \nD. 访谈法是最简单的收集数据的方法 \n', '正确答案是：访谈法是最简单的收集数据的方法, 问卷调查具有定量和定性相结合的特点, 问卷调查过程中发放的问卷越多越好  ');
INSERT INTO `problems` VALUES ('157', '有意选择某些研究对象作为样本是：（    ）', 'A. 等距抽样 \nB. 多级抽样 \nC. 单纯随机抽样 \nD. 有目的抽样 \n', '正确答案是：有目的抽样');
INSERT INTO `problems` VALUES ('158', '（  ）是研究的核心。', 'A. 研究结果 \nB. 选题 \nC. 假设 \nD. 研究对象 \n', '正确答案是：假设');
INSERT INTO `problems` VALUES ('159', '在引用他人的论文时，应在文中适当的地方注明出处，其正确顺序是（    ）', 'A. 篇名、作者、日期、页码等 \nB. 作者、日期、书刊名、页码等 \nC. 书刊名、作者、卷（期）号、页码  \nD. 作者、篇名、书刊名、日期、卷（期）号、页码等   \n', '正确答案是：作者、篇名、书刊名、日期、卷（期）号、页码等  ');
INSERT INTO `problems` VALUES ('160', '能够通过数据呈现，告诉人们某些行为、某种量的程度，为科学的评价提供可靠的依据，这体现出测量调查的（  ）。', 'A. 预测功能 \nB. 诊断功能 \nC. 评价功能 \nD. 甄选功能 \n', '正确答案是：评价功能');
INSERT INTO `problems` VALUES ('161', '（  ）是按研究对象的某一种属性的顺序排列出等级测序。', 'A. 等比量表 \nB. 类别量表 \nC. 等距量表 \nD. 顺序量表 \n', '正确答案是：顺序量表');
INSERT INTO `problems` VALUES ('162', '（  ）属于正式访谈的环节。', 'A. 加强访谈员的技巧 \nB. 准备访谈提纲 \nC. 准备访谈工具 \nD. 访谈前的预约 \n', '正确答案是：访谈前的预约');
INSERT INTO `problems` VALUES ('163', '教育行动研究是一个螺旋式加深的过程，其最后一个环节是（     ）', 'A. 计划  \nB. 行动 \nC. 反思 \nD. 问题 \n', '正确答案是：反思');
INSERT INTO `problems` VALUES ('164', '关于个案研究资料分析时要注意的问题，下列说法中错误的是（  ）。', 'A. 资料的筛选要缜密 \nB. 分析既可以采用定量也可以定性的方法 \nC.  考虑问题要主观一些 \nD. 应当避免主观性 \n', '正确答案是： 考虑问题要主观一些');
INSERT INTO `problems` VALUES ('165', '下列选项中不属于个案研究之特点的是（  ）', 'A. 研究过程的深入性 \nB. 研究数据收集方法的多样性 \nC. 研究对象的单一性 \nD. 研究结论的普遍性 \n', '正确答案是：研究结论的普遍性');
INSERT INTO `problems` VALUES ('166', '对某一事件从头至尾地进行观察，注重特定行为或事件的特征和全过程，此种观察方式是（ ）。', 'A. 标记记录 \nB. 轶事记录 \nC. 事件抽样 \nD. 连续记录 \n', '正确答案是：事件抽样');
INSERT INTO `problems` VALUES ('167', '教育观察记录除了描述记录、等级记录以外，还有（  ）。', 'A. 连续记录法 \nB. 轶事记录法 \nC. 标记记录 \nD. 日记描述法 \n', '正确答案是：标记记录');
INSERT INTO `problems` VALUES ('168', '教育观察研究设计的内容除了包括确定观察的目的、基本方法外，还主要包括（ ）。', 'A. 制定观察记录表 \nB. 学习相关的教育理论 \nC. 选择合适的观察地点 \nD. 与观察对象及时沟通 \n', '正确答案是：制定观察记录表');
INSERT INTO `problems` VALUES ('169', '将教育观察划分为直接观察和间接观察，其划分的标准是。 ', 'A. 观察内容是否有统一设计、明确具体的观察要求 \nB. 观察情境的性质 \nC. 观察时是否借助有关仪器设备 \nD. 观察者是否直接介入观察对象的活动 \n', '正确答案是：观察时是否借助有关仪器设备');
INSERT INTO `problems` VALUES ('170', '在一项关于小学一年级识字教学的行动研究中，课题组发现，玩游戏、猜字谜、编顺口溜等方法有助于提供识字效果，但拆字法对一年级学生而言有一定的难度，这是行动研究中的哪一环节（  ）。', 'A. 提出问题 \nB. 行动 \nC. 计划  \nD. 反思 \n', '正确答案是：反思');
INSERT INTO `problems` VALUES ('171', '第一次系统的将行动研究定义到教育中来，从而确立了行动研究在教育研究中的合法地位的著作是（  ）。', 'A. 《改进学校实践的行动研究》 \nB. 《美国国防教育法》 \nC. 《为了教师的研究》  \nD. 《反思型教师与行动研究》 \n', '正确答案是：《改进学校实践的行动研究》');
INSERT INTO `problems` VALUES ('172', '关于教育行动研究尽管不同学者有不同的理解，但它们却有一个共同点，即（  ）。', 'A. 强调用实证的方法对自己的行动进行研究 \nB. 强调用理论的方法对自己的行动进行研究 \nC. 强调为构建理论而进行研究 \nD. 强调为改变自身的境遇而采取行动 \n', '正确答案是：强调为改变自身的境遇而采取行动');
INSERT INTO `problems` VALUES ('173', '关于教育行动研究的具体实施中，研究结果的表述正确的是 （  ）。', 'A. 研究结果可“放诸四海而皆准” \nB. 研究结果具有一般普适性 \nC. 研究结果的应该只限于实际情境 \nD. 依据研究结果，可对不同的情境做出推论  \n', '正确答案是：研究结果的应该只限于实际情境');
INSERT INTO `problems` VALUES ('174', '每一位教师所处的情境会随人、事、物以及时空的变化为不同，研究情境不仅不同而且都有明显的独特性，这体现了教育行动研究的如下哪项特征（  ）。', 'A. 研究的目的 \nB. 研究的过程 \nC. 研究的情境 \nD. 研究的主体 \n', '正确答案是：研究的情境');
INSERT INTO `problems` VALUES ('175', '不等实验组、控制组前后测设计的基本模式是（   ）。', 'A. O1——X——O2 \nB. R  O1——X——O2 \nC. O1——X——O2         O3——-—— O4 \nD. R  O1——X——O2        R  O3——-—— O4 \n', '正确答案是：O1——X——O2         O3——-—— O4');
INSERT INTO `problems` VALUES ('176', '在教育实验研究中，教学法经常被视为（ ）', 'A. 自变量 \nB. 控制变量 \nC. 因变量  \nD. 无关变量 \n', '正确答案是：自变量');
INSERT INTO `problems` VALUES ('177', '在教育实验研究中，被试流失会影响教育实验研究的（   ）', 'A. 内在信度 \nB. 外在效度 \nC. 外在信度 \nD. 内在效度 \n', '正确答案是：内在效度');
INSERT INTO `problems` VALUES ('178', '教育实验研究结论能够得到有效推广，并能对同类现象进行解释的是（   ）。', 'A. 外在效度 \nB. 内在信度 \nC. 内在效度 \nD. 外在信度 \n', '正确答案是：外在效度');
INSERT INTO `problems` VALUES ('179', '一个独立的研究者能否在相同或相似的背景下重复研究属于（  ）', 'A. 外在信度 \nB. 内在效度 \nC. 外在效度 \nD. 内在信度 \n', '正确答案是：外在信度');
INSERT INTO `problems` VALUES ('180', '一项关于某种教学方法影响高中生化学成绩的实验研究中，因变量是（   ）。', 'A. 化学教师 \nB. 教学方法 \nC. 高中生 \nD. 化学成绩 \n', '正确答案是：化学成绩');
INSERT INTO `problems` VALUES ('181', '“微课导学对初中生科学学习效果影响”的实验研究中，“微课导学”是（  ）', 'A. 常量 \nB. 无关变量 \nC. 自变量 \nD. 因变量 \n', '正确答案是：自变量');
INSERT INTO `problems` VALUES ('182', '小明运用跟踪法，对“学业负担”进行文献检索。这里，“跟踪法”指的是（  ）。', 'A. 逆查法 \nB. 综合查找法 \nC. 顺查法 \nD. 引文查找法  \n', '正确答案是：引文查找法 ');
INSERT INTO `problems` VALUES ('183', '学术专著是（  ）。', 'A. 综合性科学研究成果  \nB. 以教育事实为主的科学研究成果  \nC. 以教育理论为主的科学研究成果 \nD. 以研究过程和发现的事实为主要内容  \n', '正确答案是：以教育理论为主的科学研究成果');
INSERT INTO `problems` VALUES ('184', '针对初中生课业负担的当前状况进行的调查属于（  ）。', 'A. 发展调查 \nB. 相关调查 \nC. 普遍调查  \nD. 现状调查 \n', '正确答案是：现状调查');
INSERT INTO `problems` VALUES ('185', '下列不属于教育叙事研究价值的是(   )。 ', 'A. 增长教育智慧 \nB. 不利于教师个体风格的形成 \nC. 促进教师专业成长 \nD. 改变教育生活状态 \n', '正确答案是：不利于教师个体风格的形成');
INSERT INTO `problems` VALUES ('186', '关于叙事研究的特点，下列说法中错误的是（ ）。', 'A. 教育叙事要对过程中的细节加以关注 \nB. 教育叙事只能是自叙 \nC. 叙述的事例是真实的、已经发生的 \nD. 教育叙事要内含教育意义 \n', '正确答案是：教育叙事只能是自叙');
INSERT INTO `problems` VALUES ('187', '下列不属于教育研究基本原则的是(   )。 ', 'A. 结果导向原则 \nB. 客观性原则 \nC. 理论联系实际原则 \nD. 伦理原则 \n', '正确答案是：结果导向原则');
INSERT INTO `problems` VALUES ('188', '只进行数据分析的研究方法是（  ）', 'A. 定性研究方法 \nB. 历史研究法 \nC. 文献研究法 \nD. 定量研究方法 \n', '正确答案是：定量研究方法');
INSERT INTO `problems` VALUES ('189', '教育研究过程的起点是（  ）。', 'A. 选择问题 \nB. 收集资料 \nC. 查阅文献 \nD. 制定计划 \n', '正确答案是：选择问题');
INSERT INTO `problems` VALUES ('190', '研究结果能比较准确地反映研究内容的特性指的是（  ）。', 'A. 效度 \nB. 内在信度 \nC. 推广度  \nD. 外在信度 \n', '正确答案是：效度');
INSERT INTO `problems` VALUES ('191', '“五（2）班的学生比五（1）班的学生归纳能力强”，从性质上看，该研究假设属于（  ）。', 'A. 一般假设 \nB. 虚无假设 \nC. 非定向假设 \nD. 特定假设 \n', '正确答案是：特定假设');
INSERT INTO `problems` VALUES ('192', '如下关于课例的含义的陈述，正确的是（  ）。', 'A. 课例就是课堂教学实录 \nB. 课例就是教学案例 \nC. 课例就是课堂观察记录 \nD. 课例就是课堂教学实例 \n', '正确答案是：课例就是课堂教学实例');
INSERT INTO `problems` VALUES ('193', '提出课堂观察“LICC”模式的人是（  ）', 'A. 贝尔斯 \nB. 顾泠沅 \nC. 弗兰德斯 \nD. 崔允漷 \n', '正确答案是：崔允漷');
INSERT INTO `problems` VALUES ('194', '当两个变量存在某种联系，其中一个变量对另一个变量具有影响作用，我们就称那个具有影响作用的变量为（ ）。', 'A. 无关变量 \nB. 自变量 \nC. 因变量 \nD. 反应变量 \n', '正确答案是：自变量');
INSERT INTO `problems` VALUES ('195', '关于文献定性分析，表述正确的是（ ）。 ', 'A. 文献研究时，对文献的性质、特征和属性作出描述和分析即可。 \nB. 文献的定性分析注重文献资料的数量程度和完整程度。 \nC. 文献的定性分析是对文献内容进行客观描述的研究方法。 \nD. 文献的定性分析研究时灵活、研究过程的规范度不高。 \n', '正确答案是：文献的定性分析研究时灵活、研究过程的规范度不高。');
INSERT INTO `problems` VALUES ('196', '为了研究《中小学教材教法》杂志载文情况，将2008-2017年发表的文章可先按年度划分十类，每类文章按理论属性或实证又分为两类。这样的文献归类方式是（  ）。   ', 'A. 主题划分 \nB. 时间划分 \nC. 连续划分 \nD. 一次划分 \n', '正确答案是：连续划分');
INSERT INTO `problems` VALUES ('197', '由研究者主动操作而变化的变量是（   ） 。', 'A. 控制变量 \nB. 因变量 \nC. 无关变量 \nD. 自变量 \n', '正确答案是：自变量');
INSERT INTO `problems` VALUES ('198', '问卷调查和访谈调查共同具有的特征是（  ） 。 ', 'A. 客观性 \nB. 统一性 \nC. 深入性 \nD. 灵活性 \n', '正确答案是：客观性');
INSERT INTO `problems` VALUES ('199', '下列不属于问卷题目制定准则的是（  ）。', 'A. 防止使用导向性问题 \nB. 在一个题目中只准包含一个问题 \nC. 复杂一些的题目总比简单一些的题目要好 \nD. 题目要清楚、不含糊，使用术语要使答卷人明白 \n', '正确答案是：复杂一些的题目总比简单一些的题目要好');
INSERT INTO `problems` VALUES ('200', '问卷调查的特点不包括（  ）。', 'A. 高效性 \nB. 统一性 \nC. 多样性 \nD. 广泛性 \n', '正确答案是：多样性');
INSERT INTO `problems` VALUES ('201', '关于问卷调查，下列选项中正确的是（  ）。', 'A. 问卷调查属于定性研究的一种 \nB. 问卷调查的问题越多越具有客观性 \nC. 问卷调查研究具有客观性的特点 \nD. 问卷调查具有主观性的特点 \n', '正确答案是：问卷调查研究具有客观性的特点');
INSERT INTO `problems` VALUES ('202', '研究者的科研活动应遵循基本的社会道德准则，不侵犯被研究者或研究参与者的利益是（   ）原则。', 'A. 定性与定量相结合 \nB. 伦理 \nC. 客观性 \nD. 创新性 \n', '正确答案是：伦理');
INSERT INTO `problems` VALUES ('203', '要求研究者根据实际具备的能力和条件确定研究问题，这体现出选题的（  ）。', 'A. 客观性原则 \nB. 创新性原则 \nC. 科学性原则 \nD. 可行性原则 \n', '正确答案是：可行性原则');
INSERT INTO `problems` VALUES ('204', '访谈准备要做的工作包括（  ）。', 'A. 尽快接近受访者 \nB. 制定访谈计划 \nC. 试谈与修改访谈提纲 \nD. 准备访谈提纲 \n', '正确答案是：制定访谈计划, 准备访谈提纲, 试谈与修改访谈提纲');
INSERT INTO `problems` VALUES ('205', '根据观察者是否直接介入观察对象的活动，教育观察可分为（ ）。', 'A. 非结构式观察 \nB. 参与性观察 \nC. 结构式观察 \nD. 非参与性观察 \n', '正确答案是：参与性观察, 非参与性观察');
INSERT INTO `problems` VALUES ('206', '在教育实验研究中，控制无关变量的主要方法有（  ）。', 'A. 随机法 \nB. 平衡法 \nC. 消除法 \nD. 恒定法 \n', '正确答案是：消除法, 平衡法, 恒定法, 随机法');
INSERT INTO `problems` VALUES ('207', '（  ）是教育调查研究的显著特点。', 'A. 广泛性 \nB. 自然性 \nC. 参考性 \nD. 间接性 \n', '正确答案是：自然性, 间接性, 广泛性, 参考性');
INSERT INTO `problems` VALUES ('208', '任何测量都必须具备的因素包括（  ）。', 'A. 测量工具 \nB. 测量规则 \nC. 测量内容 \nD. 测量客体 \n', '正确答案是：测量客体, 测量内容, 测量规则, 测量工具');
INSERT INTO `problems` VALUES ('209', '为了提高观察的针对性和观察效果，观察时常采用哪些观察方式（ ）。', 'A. 时间取样 \nB. 描述记录 \nC. 等级记录 \nD. 事件取样 \n', '正确答案是：时间取样, 事件取样');
INSERT INTO `problems` VALUES ('210', '教育行动研究在特定的情境中进行，下列关于其“研究发现与反思”步骤的叙述，正确的是（  ）。', 'A. 研究发现与反思重点是做出推论、结论 \nB. 研究发现与反思是要寻获初步的发现和作为下一阶段研究的动力 \nC. 研究发现与反思重在一点一滴地寻找改进的方法或途径 \nD. 研究发现与反思重在用来作为教育实践、个人专业成长、进一步研究的依据 \n', '正确答案是：研究发现与反思重在一点一滴地寻找改进的方法或途径, 研究发现与反思重在用来作为教育实践、个人专业成长、进一步研究的依据, 研究发现与反思是要寻获初步的发现和作为下一阶段研究的动力');
INSERT INTO `problems` VALUES ('211', '根据Mcniff,Lomax & Whitehead的研究结果，下述关于协同合作阶段中“你自己的角色”的表述，正确的是（  ）。', 'A. 做好去“冒险”的准备 \nB. 应认清自己角色 \nC. 你需考虑别人所扮演的角色，以及建立明确的伦理守则，来进行你的研究 \nD. 保持开放 \n', '正确答案是：保持开放, 做好去“冒险”的准备, 你需考虑别人所扮演的角色，以及建立明确的伦理守则，来进行你的研究');
INSERT INTO `problems` VALUES ('212', '影响实验内在效度的因素主要来自于（  ）。', 'A. 历史或经历 \nB. 统计回归 \nC. 被试选择 \nD. 发展与成熟  \n', '正确答案是：历史或经历, 发展与成熟 , 被试选择, 统计回归');
INSERT INTO `problems` VALUES ('213', '教育档案包括（ ）。', 'A. 手册 \nB. 会议文献 \nC. 年鉴 \nD. 学位论文 \n', '正确答案是：年鉴, 会议文献, 学位论文');
INSERT INTO `problems` VALUES ('214', '下列关于教育叙事的表述，正确的是（ ）。', 'A. 教育叙事是以人物及其所感所想为主线 \nB. 教育叙事要注意情节性 \nC. 叙述事例可以进行适当虚构 \nD. 教育叙事要有教育意义 \n', '正确答案是：教育叙事是以人物及其所感所想为主线, 教育叙事要注意情节性, 教育叙事要有教育意义');
INSERT INTO `problems` VALUES ('215', '教育研究的特征包括（      ）。', 'A. 教育研究过程的实践性 \nB. 教育研究结果的后效性 \nC. 教育研究方法的整合性 \nD. 教育研究基础的包容性 \n', '正确答案是：教育研究基础的包容性, 教育研究过程的实践性, 教育研究结果的后效性, 教育研究方法的整合性');
INSERT INTO `problems` VALUES ('216', '学术论文写作中，需要注意的问题是(   )。 ', 'A. 立论、推论和表述的科学性 \nB. 典型分析和一般分析的结合 \nC. 数据和文字表述的统一性 \nD. 论点、论据和论述的逻辑性 \n', '正确答案是：立论、推论和表述的科学性, 论点、论据和论述的逻辑性, 数据和文字表述的统一性, 典型分析和一般分析的结合');
INSERT INTO `problems` VALUES ('217', '以下关于问卷调查的选项，错误的是（  ）。', 'A. 问卷调查属于定性研究的一种 \nB. 问卷调查研究具有客观性的特点 \nC. 问卷调查的问题越多越具有客观性 \nD. 问卷调查具有主观性的特点 \n', '正确答案是：问卷调查属于定性研究的一种, 问卷调查具有主观性的特点, 问卷调查的问题越多越具有客观性');
INSERT INTO `problems` VALUES ('218', '“总数N=500，样本容量n=50，求出间隔500÷50=10，于是每隔10个抽取一个样本，连续抽样50次”。这是采用（  ）', 'A. 多段随机取样法 \nB. 简单随机取样法 \nC. 等距随机取样法  \nD. 分层随机取样法 \n', '正确答案是：等距随机取样法 ');
INSERT INTO `problems` VALUES ('219', '教育研究中假设的作用在于（  ）。', 'A. 确定自变量的操作性定义 \nB. 明确研究的范围 \nC. 清除被试的特征 \nD. 使问题更具体、方向更明确 \n', '正确答案是：使问题更具体、方向更明确');
INSERT INTO `problems` VALUES ('220', '下列不属于主要定性研究方法的是（ ）', 'A. 扎根理论 \nB. 现象学研究 \nC. 问卷调查法 \nD. 叙事研究 \n', '正确答案是：问卷调查法');
INSERT INTO `problems` VALUES ('221', '教育研究报告中所列参考的书籍，表述的主要项目和顺序是（    ）', 'A. 作者姓名、书名、出版单位和时间、页数 \nB. 出版单位和时间、作者姓名、书名、页数 \nC. 书名、作者姓名、出版单位和时间、页数 \nD. 出版单位和时间、作者姓名、页数、书名 \n', '正确答案是：作者姓名、书名、出版单位和时间、页数');
INSERT INTO `problems` VALUES ('222', '（  ）是对事物作出量化描述，对非量化事物量化赋值的过程。', 'A. 访谈调查 \nB. 统计 \nC. 问卷调查 \nD. 测量 \n', '正确答案是：测量');
INSERT INTO `problems` VALUES ('223', '根据事物的某一特点对事物属性进行分类，用数字和符号来代表事物或性质，这种量表属于（  ）。', 'A. 等比量表 \nB. 顺序量表 \nC. 等距量表 \nD. 类别量表 \n', '正确答案是：类别量表');
INSERT INTO `problems` VALUES ('224', '下列说法不正确的是（  ）。', 'A. 创造友好气氛，与被访谈者谈的越投机越好 \nB. 在访谈时，要把握住方向和主题，随时进行记录 \nC. 访谈者在访谈期间最好作仔细的访谈记录 \nD. 进行访谈时，要首先介绍自己的身份 \n', '正确答案是：创造友好气氛，与被访谈者谈的越投机越好');
INSERT INTO `problems` VALUES ('225', '与传统教育研究范式相比较，教育行动研究是在哪里做研究的（  ）。', 'A. 变量可以得到控制的环境中 \nB. 其它三个选项都不对 \nC. 任意环境中 \nD. 课堂情境中 \n', '正确答案是：课堂情境中');
INSERT INTO `problems` VALUES ('226', '访谈者能够根据被访谈者的反映，对访谈问题做出适当的调整，这体现出访谈调查具有（  ）。', 'A. 准确性 \nB. 灵活性 \nC. 真实性 \nD. 深入性 \n', '正确答案是：灵活性');
INSERT INTO `problems` VALUES ('227', '对某个人或某个团体、机构运用多种手段进行较为长期的跟踪研究，弄清研究问题的现状、原因乃至如何进行干预的研究方法是（  ）', 'A. 经验总结法 \nB. 个案研究法 \nC. 访谈法 \nD. 调查表法 \n', '正确答案是：个案研究法');
INSERT INTO `problems` VALUES ('228', '下列说法属于个案研究局限性的是（ ）', 'A. 过程更体现生态化 \nB. 为特殊事件的研究所必需 \nC. 难以重复验证 \nD. 结果更具有有针对性 \n', '正确答案是：难以重复验证');
INSERT INTO `problems` VALUES ('229', '按照研究对象分，个案研究可以划分为（  ）', 'A. 个体个案研究、团体个案研究、机构个案研究 \nB. 个体个案研究、机构个案研究 \nC. 个体个案研究、团体个案研究、实验个案研究 \nD. 个体个案研究、团体个案研究 \n', '正确答案是：个体个案研究、团体个案研究、机构个案研究');
INSERT INTO `problems` VALUES ('230', '下列关于个案研究的说法错误的是（ ）。', 'A. 个案研究的手段是单一的  \nB. 个案研究对象是单一的 \nC. 个案研究的结果更具针对性   \nD. 个案研究的过程是深入的  \n', '正确答案是：个案研究的手段是单一的 ');
INSERT INTO `problems` VALUES ('231', '下列说法属于个案研究优势的是（  ）。', 'A. 结论的代表性 \nB. 难以重复验证 \nC. 结果更具有针对性 \nD. 主观性较强 \n', '正确答案是：结果更具有针对性');
INSERT INTO `problems` VALUES ('232', '我国著名的幼儿教育家陈鹤琴以自己的孩子为观察对象研究儿童的一般发展，主要采用的观察记录方法是（  ）', 'A. 轶事记录法 \nB. 描述记录法 \nC. 日记描述法 \nD. 连续记录法 \n', '正确答案是：日记描述法');
INSERT INTO `problems` VALUES ('233', '教育观察除了要遵循目的性原则、客观性原则外，还需遵循的原则是（ ）。', 'A. 全面性原则 \nB. 自然性原则 \nC. 主观性原则 \nD. 自觉性原则 \n', '正确答案是：自觉性原则');
INSERT INTO `problems` VALUES ('234', '将教育观察划分为参与性观察和非参与性观察，其划分的标准是（  ） 。', 'A. 观察内容是否有统一设计、明确具体的观察要求 \nB. 观察时是否借助有关仪器设备 \nC. 观察情境的性质 \nD. 观察者是否直接介入观察对象的活动 \n', '正确答案是：观察者是否直接介入观察对象的活动');
INSERT INTO `problems` VALUES ('235', '在一项关于高中语文翻转课堂教学策略的行动研究中，王老师收集了“教学资源与导学准备”环节的有关研究数据，这是行动研究中的哪一环节（  ）。', 'A. 行动 \nB. 计划  \nC. 提出问题 \nD. 反思 \n', '正确答案是：行动');
INSERT INTO `problems` VALUES ('236', '行动研究的概念，最早是由（  ）引入中国大陆。 ', 'A. 陈立 \nB. 林天佑 \nC. 蒋楠 \nD. 申继亮 \n', '正确答案是：陈立');
INSERT INTO `problems` VALUES ('237', '如下关于教育行动研究的理解，正确的是（  ）。  ', 'A. 教育行动研究的主体是高校教师  \nB. 教育行动研究是一个线性的研究过程 \nC. 教育行动研究者针对理论问题开展研究 \nD. 行动研究者在实际教育情境中开展研究 \n', '正确答案是：行动研究者在实际教育情境中开展研究');
INSERT INTO `problems` VALUES ('238', '“充分表达问题改善的必要性”是对下列关于教育行动研究哪一项目的评价标准（   ）。', 'A. 研究题目 \nB. 研究动机 \nC. 研究目的 \nD. 研究意义 \n', '正确答案是：研究动机');
INSERT INTO `problems` VALUES ('239', '下列正确描述了教育行动研究特征的是（  ）。', 'A. 强调对行动的反思 \nB. 研究过程不需要理性指导 \nC. 研究结论具有普适性 \nD. 行动过程就是研究过程 \n', '正确答案是：强调对行动的反思');
INSERT INTO `problems` VALUES ('240', '描述数据集中趋势的统计量称为（    ）', 'A. 地位量数 \nB. 相关量数 \nC. 集中量数 \nD. 差异量数 \n', '正确答案是：集中量数');
INSERT INTO `problems` VALUES ('241', '随机分配实验组、控制组后测设计的基本模式是（  ）。', 'A. R    X——O1           R       O2 \nB. O1——X——O2 \nC. R  O1——X——O2          R  O3——-—— O4 \nD. R  O1——X——O2 \n', '正确答案是：R    X——O1           R       O2');
INSERT INTO `problems` VALUES ('242', '要了解直观性教学的内在规律、因果关系、应选择的研究方法是（   ）。', 'A. 观察法 \nB. 实验研究法 \nC. 历史研究法 \nD. 调查法 \n', '正确答案是：实验研究法');
INSERT INTO `problems` VALUES ('243', '在实验过程中，为最大程度地保证内在效度，应尽可能控制（  ）。', 'A. 自变量 \nB. 实验变量 \nC. 无关变量 \nD. 因变量 \n', '正确答案是：无关变量');
INSERT INTO `problems` VALUES ('244', '在教育实验发展过程中，芝加哥实验学校的创立者是（   ）。', 'A. 桑代克 \nB. 吉德 \nC. 福禄培尔 \nD. 杜威 \n', '正确答案是：杜威');
INSERT INTO `problems` VALUES ('245', '下列哪一项不是影响实验内在效度的因素（  ）', 'A. 历史或经历 \nB. 发展与成熟 \nC. 被试选择  \nD. 实验情境的人为性 \n', '正确答案是：实验情境的人为性');
INSERT INTO `problems` VALUES ('246', '影响教育实验研究外在效度的因素是（   ）。', 'A. 被试选择 \nB. 被试效应 \nC. 被试流失 \nD. 被试样本缺乏代表性 \n', '正确答案是：被试样本缺乏代表性');
INSERT INTO `problems` VALUES ('247', '研究结果的稳定性或一致性指的是（  ）', 'A. 外在效度 \nB. 内在效度 \nC. 信度 \nD. 推广度  \n', '正确答案是：信度');
INSERT INTO `problems` VALUES ('248', '教育实验中的无关变量是指（   ）。', 'A. 与自变量无关的变量 \nB. 自变量以外一切可能影响因变量的变量 \nC. 与实验无关的变量 \nD. 与因变量无关的变量 \n', '正确答案是：自变量以外一切可能影响因变量的变量');
INSERT INTO `problems` VALUES ('249', '“清晰的教学目标有助于提升小学生的学习效果”的陈述中，其中因变量是（   ）。', 'A. 学习方式 \nB. 学习效果 \nC. 教学方式 \nD. 教学目标 \n', '正确答案是：学习效果');
INSERT INTO `problems` VALUES ('250', '“探究学习对教学效果影响”的实验研究中，“探究学习”是（  ）', 'A. 无关变量 \nB. 自变量 \nC. 常量 \nD. 因变量 \n', '正确答案是：自变量');
INSERT INTO `problems` VALUES ('251', '教育科学文献中品种最多、数量最大、历史最长的情报源是（  ）。 ', 'A. 手册 \nB. 期刊 \nC. 参考工具书  \nD. 年鉴 \n', '正确答案是：手册');
INSERT INTO `problems` VALUES ('252', '以下（  ）法属于教育调查研究中的一种。', 'A. 访谈 \nB. 行动研究 \nC. 叙事研究 \nD. 实验 \n', '正确答案是：访谈');
INSERT INTO `problems` VALUES ('253', '教育叙事的实践叙事观认为（  ）', 'A. 叙事更多地偏向“研究”而非“故事” \nB. 叙事指向理论建构  \nC. 叙事就是讲自己的故事 \nD. 叙事重在讲述所发生的事实 \n', '正确答案是：叙事重在讲述所发生的事实');
INSERT INTO `problems` VALUES ('254', '下列不属于教育叙事题材来源的是（ ）。', 'A. 课堂教学方面的题材 \nB. 班级管理方面的题材 \nC. 学校管理方面的题材 \nD. 家庭教育方面的题材 \n', '正确答案是：家庭教育方面的题材');
INSERT INTO `problems` VALUES ('255', '现代教育研究主要采用的是（   ）研究方法。', 'A. 直观观察 \nB. 混合 \nC. 实证 \nD. 实验与分析 \n', '正确答案是：实证');
INSERT INTO `problems` VALUES ('256', '下列属于实证研究法的是（    ）。', 'A. 演绎法 \nB. 归纳法 \nC. 观察法 \nD. 文献研究法 \n', '正确答案是：观察法');
INSERT INTO `problems` VALUES ('257', '选题虽然没有固定不变的模式和步骤，但一般包括：①查阅相关资料；②确定研究领域；③陈述研究问题；④界定研究问题的范围这几个方面，研究者在选题过程中一般遵循的程序是（   ）。', 'A. ②①④③ \nB. ②①③④ \nC. ①②③④  \nD. ③④①② \n', '正确答案是：②①④③');
INSERT INTO `problems` VALUES ('258', '教育研究的逻辑起点是（    ）。', 'A. 教育实践的需要 \nB. 个人兴趣的激发 \nC. 研究问题 \nD. 教育知识创新的驱动 \n', '正确答案是：研究问题');
INSERT INTO `problems` VALUES ('259', '下列关于课例研究的说法正确的是（ ）。', 'A.  课例研究离不开课堂观察技术 \nB. 课例研究的实践模式相对单一 \nC. 课例研究也称为教学案例研究 \nD. 课例研究的实施过程是相同的 \n', '正确答案是： 课例研究离不开课堂观察技术');
INSERT INTO `problems` VALUES ('260', '课堂观察是一种行为系统、一种工作流程。此种观点是从什么角度得出的（ ）。', 'A. 研究过程 \nB. 工作性质 \nC. 方法论 \nD. 研究成果 \n', '正确答案是：研究过程');
INSERT INTO `problems` VALUES ('261', '常见的课堂观察记录工具除了表单式、划记式、编码式与流程式外，还有（  ）。', 'A. 时间抽样 \nB. 日记描述 \nC. 事件抽样 \nD. 综合式 \n', '正确答案是：综合式');
INSERT INTO `problems` VALUES ('262', '控制变量是指（   ）。', 'A. 无关变量 \nB. 因变量 \nC. 自变量 \nD. 结果变量 \n', '正确答案是：无关变量');
INSERT INTO `problems` VALUES ('263', '准实验与真实验的主要区别在于（  ）。', 'A. 变量之间的关系不同 \nB. 被试抽取方式不同 \nC. 研究目的不同 \nD. 变量个数不同 \n', '正确答案是：被试抽取方式不同');
INSERT INTO `problems` VALUES ('264', '关于问卷编制，下面哪种说法不正确（  ）。', 'A. 题目不要太多 \nB. 问题表述要简洁 \nC. 避免使用让人感到紧张或有压力的用语 \nD. 要尽量使用专业性术语 \n', '正确答案是：要尽量使用专业性术语');
INSERT INTO `problems` VALUES ('265', '问卷调查对所有对象都以同一种格式进行询问，这体现出（  ）。', 'A. 广泛性 \nB. 高效性 \nC.  统一性 \nD. 客观性 \n', '正确答案是： 统一性');
INSERT INTO `problems` VALUES ('266', '下列不属于收集研究数据工具的是（   ）。', 'A. 访谈 \nB. 实物分析 \nC. 定量研究 \nD. 观察  \n', '正确答案是：定量研究');
INSERT INTO `problems` VALUES ('267', '下列不属于教育叙事研究常用方法的是（  ）。', 'A. 访谈法 \nB. 个案研究法 \nC. 观察法 \nD. 实验法 \n', '正确答案是：实验法');
INSERT INTO `problems` VALUES ('268', '观察结果呈现时，需将观察对象的真实姓名转变为化名，此种做法遵循了（    ）。', 'A. 主观性原则 \nB. 伦理原则 \nC. 客观性原则 \nD. 量化原则 \n', '正确答案是：伦理原则');
INSERT INTO `problems` VALUES ('269', '对研究方法的选择起决定性的是 (   ) 。', 'A. 研究过程 \nB. 研究手段 \nC. 研究问题 \nD. 研究技术 \n', '正确答案是：研究问题');
INSERT INTO `problems` VALUES ('270', '尽快接近被访者的方法可以是（  ）。', 'A. 自我介绍 \nB. 登门拜访 \nC. 预约 \nD. 请熟悉的人引荐 \n', '正确答案是：请熟悉的人引荐, 自我介绍');
INSERT INTO `problems` VALUES ('271', '根据观察情境的性质不同，教育观察可分为（ ）。', 'A. 实验室观察 \nB. 自然情境观察 \nC. 间接观察 \nD. 直接观察 \n', '正确答案是：自然情境观察, 实验室观察');
INSERT INTO `problems` VALUES ('272', '给变量下操作性定义的方法主要有（   ）。', 'A. 主体描述法 \nB. 条件描述法 \nC. 指标描述法 \nD. 行为描述法 \n', '正确答案是：条件描述法, 指标描述法, 行为描述法');
INSERT INTO `problems` VALUES ('273', '文献的分析包括定性和定量两种方式。下列属于定量分析的资料是（  ）。', 'A. 信件 \nB. 教案 \nC. 调研报告 \nD. 各种教科书 \n', '正确答案是：各种教科书, 教案, 调研报告');
INSERT INTO `problems` VALUES ('274', '等级记录也称为评价记录，在观察过程中常用（ ）。', 'A. 事件取样 \nB. 图标评价法 \nC. 数字等级法 \nD. 时间取样 \n', '正确答案是：数字等级法, 图标评价法');
INSERT INTO `problems` VALUES ('275', '一份观察计划主要包括的内容有（ ）。', 'A. 观察目的 \nB. 观察记录表 \nC. 观察对象 \nD. 观察内容 \n', '正确答案是：观察目的, 观察对象, 观察内容, 观察记录表');
INSERT INTO `problems` VALUES ('276', '下列关于教育行动研究的步骤的观点，正确的是（  ）。', 'A. 计划、行动、观察、反思 \nB. 确定研究题目、说明研究动机与目的、进行文献探讨、研究设计与实施、研究发现与反思、进一步研究构想 \nC. 发现和界定问题、制定行动计划、实施和记录行动、反思和评价 \nD. 计划、实践、观察、反思、下一步行动建议 \n', '正确答案是：发现和界定问题、制定行动计划、实施和记录行动、反思和评价, 计划、行动、观察、反思, 确定研究题目、说明研究动机与目的、进行文献探讨、研究设计与实施、研究发现与反思、进一步研究构想, 计划、实践、观察、反思、下一步行动建议');
INSERT INTO `problems` VALUES ('277', '按照假设的性质分,可将假设分为（   ）。', 'A. 一般假设 \nB. 特定假设 \nC. 虚无假设 \nD. 定向假设 \nE. 非定向假设 \n', '正确答案是：特定假设, 一般假设, 虚无假设');
INSERT INTO `problems` VALUES ('278', '教育实验研究方法是教育实验研究报告的重要内容之一，一般包括(    )。 ', 'A. 对无关变量的控制情况说明 \nB. 实验的类型 \nC. 被试选择 \nD. 实验的具体步骤说明 \n', '正确答案是：被试选择, 实验的类型, 实验的具体步骤说明, 对无关变量的控制情况说明');
INSERT INTO `problems` VALUES ('279', '教育调查的一般步骤包括（  ）。', 'A. 实施调查研究 \nB. 确定调查课题 \nC. 编制调查工具 \nD. 选择调查对象 \n', '正确答案是：确定调查课题, 选择调查对象, 编制调查工具, 实施调查研究');
INSERT INTO `problems` VALUES ('280', '近代教育研究采取的主要研究方法是（   ）。', 'A. 归纳 \nB. 演绎 \nC. 综合 \nD. 分析 \nE. 实验 \n', '正确答案是：分析, 综合');
INSERT INTO `problems` VALUES ('281', '教育研究按结果取向分类可以分成 （  ）。', 'A. 教师事实研究 \nB. 描述性研究 \nC. 探索性研究 \nD. 教育价值研究 \n', '正确答案是：探索性研究, 描述性研究');
INSERT INTO `problems` VALUES ('282', '教育研究问题的来源有（  ）。', 'A. 从各级规划课题中选择问题 \nB. 从教育实践中发现问题 \nC. 从过去的研究成果中寻找值得研究的问题 \nD. 从有关理论中演绎出研究问题 \nE. 从留有争议的问题中寻找研究问题 \n', '正确答案是：从教育实践中发现问题, 从过去的研究成果中寻找值得研究的问题, 从有关理论中演绎出研究问题, 从留有争议的问题中寻找研究问题, 从各级规划课题中选择问题');
INSERT INTO `problems` VALUES ('283', '前实验设计的特点是（  ）。', 'A. 严格控制无关变量  \nB. 被试不是随机的 \nC. 能操控实验变量 \nD. 不能控制无关变量 \n', '正确答案是：被试不是随机的, 不能控制无关变量, 能操控实验变量');
INSERT INTO `problems` VALUES ('284', '下列表述符合样本选择基本要求的是（ ）。', 'A. 样本越大越好 \nB. 抽样要有代表性 \nC. 要合理确定样本的容量 \nD. 样本要有代表性 \n', '正确答案是：样本要有代表性, 抽样要有代表性, 要合理确定样本的容量');
INSERT INTO `problems` VALUES ('285', '把对象总体分成不重叠的层次，然后从每一阶层中选取抽样样本是（   ）', 'A. 整群抽样 \nB. 分层随机抽样 \nC. 简单随机抽样 \nD. 机械抽样 \n', '正确答案是：分层随机抽样');
INSERT INTO `problems` VALUES ('286', '如下是鲁艺的毕业论文中参考文献的书写格式，其中正确的是（  ）', 'A. 陈鹤琴．儿童心理之研究[D]．商务印书馆，1925：7-8． \nB. 黄敏．对张力的描述性评论[N]．全球教育展望，2009（6）：15． \nC. 张小凡．我的儿童研究故事[J]．教师月刊，2012（11）：16． \nD. 陈群．儿童心理学[M]．华东师范大学出版社，2005：29． \n', '正确答案是：张小凡．我的儿童研究故事[J]．教师月刊，2012（11）：16．');
INSERT INTO `problems` VALUES ('287', '关于访谈问题，下列选项中错误的是（ ）。', 'A. 访谈问题不应当具有导向性 \nB. 访谈问题应当避免“是”、“不是”等封闭性问题 \nC. 访谈的问题越复杂越能收集到有效信息 \nD. 访谈问题不应连续发问 \n', '正确答案是：访谈的问题越复杂越能收集到有效信息');
INSERT INTO `problems` VALUES ('288', '（  ）不属于访谈调查的特点。', 'A. 真实性 \nB. 统一性 \nC. 客观性 \nD. 准确性 \n', '正确答案是：统一性');
INSERT INTO `problems` VALUES ('289', '对研究对象的一些典型特征作全面、深入的考察和分析的研究方法是（    ）', 'A. 访谈法 \nB. 个案研究法 \nC. 经验总结法  \nD. 调查表法 \n', '正确答案是：个案研究法');
INSERT INTO `problems` VALUES ('290', '下列说法属于个案研究特点的是（ ）。', 'A. 研究对象的单一性 \nB. 研究手段的单一性 \nC. 研究过程的普遍性 \nD. 研究对象的复杂性 \n', '正确答案是：研究对象的单一性');
INSERT INTO `problems` VALUES ('291', '观察学生课堂行为表现，把上课时间45分钟划分为9段，每一段5分钟，记录每一时间段内学生做了什么，比如，认真听课、举手回答问题等。此种观察运用了哪种观察方式（ ）。', 'A. 行为抽样 \nB. 学生抽样 \nC. 时间抽样 \nD. 事件抽样 \n', '正确答案是：时间抽样');
INSERT INTO `problems` VALUES ('292', '在观察研究中，着重记录某种有价值的行为的记录方法是（   ）', 'A. 日记记录法 \nB. 描述记录法 \nC. 轶事记录法 \nD. 联系记录法 \n', '正确答案是：轶事记录法');
INSERT INTO `problems` VALUES ('293', '教育观察的特点，除了目的性、计划性、观察记录的翔实性以外，还包括（  ）。', 'A. 主观性 \nB. 日常性 \nC. 问题性 \nD. 客观性 \n', '正确答案是：客观性');
INSERT INTO `problems` VALUES ('294', '如下选项中，不是撰写教育行动研究动机参考标准的是（  ）。', 'A. 改进一项教育措施 \nB. 建构一个教育理论 \nC. 创新一项教育措施 \nD. 解决一个教育问题 \n', '正确答案是：建构一个教育理论');
INSERT INTO `problems` VALUES ('295', '提出“没有无行动的研究，也没有无研究的行动”的是（  ）。', 'A. 白金汉姆  \nB. 麦克尔南 \nC. 勒温 \nD. 科里 \n', '正确答案是：勒温');
INSERT INTO `problems` VALUES ('296', '在实际情境中，由学校教师和校外专家共同合作，针对实际问题提出改进计划，通过在实践中实施、验证、修正而得到研究结果的一种研究方法是（  ）', 'A. 教育行动研究 \nB. 叙事研究 \nC. 个案研究法 \nD. 课例研究 \n', '正确答案是：教育行动研究');
INSERT INTO `problems` VALUES ('297', '下列选项中，哪一项不是教育行动研究题目的评价标准（   ）。', 'A. 适度表达改善问题的意图 \nB. 意义明确 \nC. 符合社会发展需要 \nD. 文字清楚 \n', '正确答案是：符合社会发展需要');
INSERT INTO `problems` VALUES ('298', '教育行动研究的主体是(   )。', 'A. 大学教授 \nB. 既不是教师，也不是大学教授 \nC. 教师和大学教授 \nD. 教师  \n', '正确答案是：教师 ');
INSERT INTO `problems` VALUES ('299', '教育实验研究报告的核心是（  ）。', 'A. 实验方法 \nB. 实验结果 \nC. 结论 \nD. 分析与讨论 \n', '正确答案是：实验结果');
INSERT INTO `problems` VALUES ('300', '随机分配实验组、控制组前后测设计的基本模式是（   ）。', 'A. O1——X——O2           O3——-—— O4 \nB. R  O1——X——O2          R  O3——-—— O4 \nC. O1——X——O2 \nD. R  O1——X——O2 \n', '正确答案是：R  O1——X——O2          R  O3——-—— O4');
INSERT INTO `problems` VALUES ('301', '教育实验研究属于（ ）。', 'A. 定量研究 \nB. 行动研究 \nC. 混合研究 \nD. 定性研究 \n', '正确答案是：定量研究');
INSERT INTO `problems` VALUES ('302', '影响实验外在效度的因素是（  ）', 'A. 发展与成熟 \nB. 被试选择 \nC. 历史或经历 \nD. 实验情境的人为性 \n', '正确答案是：实验情境的人为性');
INSERT INTO `problems` VALUES ('303', ' 在“学校地理分布影响初二年级学生对学校态度的研究”的陈述中，其中因变量是（  ）。', 'A. 城镇 \nB. 学生对学校的态度 \nC. 乡村 \nD. 城市 \n', '正确答案是：学生对学校的态度');
INSERT INTO `problems` VALUES ('304', '“翻转课堂对大学生学习效果影响”的实验研究中，“翻转课堂”是（  ）', 'A. 无关变量 \nB. 因变量 \nC. 自变量 \nD. 常量 \n', '正确答案是：自变量');
INSERT INTO `problems` VALUES ('305', '通过研究作者的立场、基本思想和文献形成的具体环境来判断作者记述的客观性和倾向性的方式是（  ）。', 'A. 证真评论 \nB. 内审法 \nC. 外审法 \nD. 辨伪评论 \n', '正确答案是：内审法');
INSERT INTO `problems` VALUES ('306', '“初中学生自我意识发展的调查研究”，从调查目的来看，该调查是（     ）。', 'A. 现状调查 \nB. 相关调查 \nC. 问卷调查 \nD. 发展调查 \n', '正确答案是：现状调查');
INSERT INTO `problems` VALUES ('307', '下列哪种不是教育叙事的观点（  ）。', 'A. 综合叙事观 \nB. 理论叙事观 \nC. 实践叙事观 \nD. 宏大叙事观 \n', '正确答案是：宏大叙事观');
INSERT INTO `problems` VALUES ('308', '开展教育科学研究最重要的前提是（ ）', 'A. 确定研究内容 \nB. 找到研究的问题 \nC. 确定研究的技术路线 \nD. 确定研究对象 \n', '正确答案是：找到研究的问题');
INSERT INTO `problems` VALUES ('309', '一位小学语文老师想进行只需要描述的研究方法开展研究，你认为这样的研究方法属于（  ）。', 'A. 文献研究法 \nB. 定量研究方法 \nC. 定性研究方法 \nD. 实验研究法 \n', '正确答案是：定性研究方法');
INSERT INTO `problems` VALUES ('310', '教育研究始于（  ）。', 'A. 查阅文献 \nB. 搜集资料 \nC. 选题 \nD. 研究对象选择 \n', '正确答案是：选题');
INSERT INTO `problems` VALUES ('311', '“七（2）班的学生比七（1）班的学生逻辑推理能力强”，从性质上看，该研究假设属于（  ）。', 'A. 一般假设 \nB. 特定假设 \nC. 非定向假设 \nD. 虚无假设 \n', '正确答案是：特定假设');
INSERT INTO `problems` VALUES ('312', '下列关于课例研究的价值说法错误的是（ ）。', 'A. 可以实现经验共享 \nB. 课例研究对课堂教改的效果不大 \nC. 可以促进理论与实践的结合 \nD. 可以提供集体研讨的平台 \n', '正确答案是：课例研究对课堂教改的效果不大');
INSERT INTO `problems` VALUES ('313', '有教师运用LICC模式进行课堂观察，在该模式中除了包括学生学习、教师教学、课程性质以外，还包括（ ）。', 'A. 课堂文化 \nB. 动机 \nC. 情感 \nD. 管理 \n', '正确答案是：课堂文化');
INSERT INTO `problems` VALUES ('314', '对文献中记载的内容是否属实的鉴别，主要是通过（  ）完成。 ', 'A. 第三方鉴别法 \nB. 内审法 \nC. 外审法 \nD. 转译法 \n', '正确答案是：内审法');
INSERT INTO `problems` VALUES ('315', '控制无关变量的教育实验中，在选择被试、安排实验处理顺序等许多实验环节上不受实验人员主观意图的影响而由偶然机遇决定的方法是（   ）。', 'A. 平衡法 \nB. 消除法 \nC. 恒定法 \nD. 随机法 \n', '正确答案是：随机法');
INSERT INTO `problems` VALUES ('316', '在教育实验设计中，能随机选择和安排被试，严格控制无关变量，有效操控实验变量的的实验是（  ）。', 'A. 真实验 \nB. 准实验 \nC. 前实验 \nD. 探索性实验 \n', '正确答案是：真实验');
INSERT INTO `problems` VALUES ('317', '问卷的主体是（ ）。', 'A. 问题 \nB. 知情同意信 \nC. 指导语 \nD. 结束语 \n', '正确答案是：问题');
INSERT INTO `problems` VALUES ('318', '“有人认为我国目前的新课改在实施过程当中存在过分注重形式的现象，应该改变这种现状。你怎么看待这个问题？”这一问题的设置违反了问卷设计的（  ）原则。', 'A. 避免一题多问 \nB. 问题数量适中且顺序合理 \nC. 内容简单而详尽 \nD. 避免诱导 \n', '正确答案是：避免一题多问');
INSERT INTO `problems` VALUES ('319', '研究资料的整理，不管是定性还是定量资料的整理，第一步都是（   ）', 'A. 资料分析 \nB. 资料分类 \nC. 资料汇总 \nD. 资料审核 \n', '正确答案是：资料审核');
INSERT INTO `problems` VALUES ('320', '访谈者在没有征求访谈对象意见的情况下，悄悄地将访谈内容进行了录音，这种做法违背了教育研究的基本原则是（   ）。', 'A. 创新性原则 \nB. 理论联系实际原则   \nC. 伦理性原则 \nD. 客观性原则 \n', '正确答案是：伦理性原则');
INSERT INTO `problems` VALUES ('321', '下列说法中错误的是（ ）', 'A. 访谈要进行详细的准备 \nB. 教育叙事研究必须具有教育意义   \nC. 研究问题决定研究方法 \nD. 观察法是一种定量研究方法 \n', '正确答案是：观察法是一种定量研究方法');
INSERT INTO `problems` VALUES ('322', '制定访谈计划需要考虑（  ）方面。', 'A. 选择访谈方式 \nB. 加强访谈员的技能 \nC. 考虑访谈的时间与环境 \nD. 考虑访谈内容 \n', '正确答案是：选择访谈方式, 考虑访谈内容, 考虑访谈的时间与环境');
INSERT INTO `problems` VALUES ('323', '教育观察研究应注意的问题有（ ）。', 'A. 坚持观察的客观性 \nB. 坚持观察的主观性 \nC. 观察记录要准确、清晰 \nD. 做好观察前的准备工作 \n', '正确答案是：做好观察前的准备工作, 坚持观察的客观性, 观察记录要准确、清晰');
INSERT INTO `problems` VALUES ('324', '在“不同教育水平的师生课堂交流研究”的陈述中，其中自变量是（   ）。', 'A. 小学 \nB. 测量出的师生课堂交流各项指标的分数 \nC. 初中 \nD. 高中 \n', '正确答案是：小学, 初中, 高中');
INSERT INTO `problems` VALUES ('325', '参考文献是文献综述不可缺少的一环。关于参考文献的描述，不正确的有。', 'A. 收集资料要瞄准主流，其他文献可以完全忽略 \nB. 参考文献的年限越久远就好 \nC. 参考文献的引用数目越多越好 \nD. 参考文献应是文中引用过的、能反映主题全貌的并且是作者直接阅读过的文献资料 \n', '正确答案是：参考文献的引用数目越多越好, 参考文献的年限越久远就好, 收集资料要瞄准主流，其他文献可以完全忽略');
INSERT INTO `problems` VALUES ('326', '描述记录法主要包括（ ）。', 'A. 轶事记录法 \nB. 连续记录法 \nC. 日记描述法 \nD. 事件取样 \n', '正确答案是：日记描述法, 轶事记录法, 连续记录法');
INSERT INTO `problems` VALUES ('327', '一个完整的课例通常呈现的结构是（ ）。', 'A. 诠释与研究 \nB. 主题与背景 \nC. 课堂情境与描述 \nD. 问题与讨论 \n', '正确答案是：主题与背景, 课堂情境与描述, 问题与讨论, 诠释与研究');
INSERT INTO `problems` VALUES ('328', '教育行动研究的中国化发展经历了哪几个阶段（  ）。', 'A. 中国本土化阶段 \nB. 译介引入阶段 \nC. 发展推广阶段 \nD. 教育行动研究的多样化发展与应用阶段 \n', '正确答案是：译介引入阶段, 发展推广阶段, 教育行动研究的多样化发展与应用阶段');
INSERT INTO `problems` VALUES ('329', '教育行动研究的目的是（  ）。', 'A. 关注理论上的建构 \nB. 为了实践本身的改善 \nC. 增进对现状的了解 \nD. 解决教育中的实际问题 \n', '正确答案是：解决教育中的实际问题, 为了实践本身的改善, 增进对现状的了解');
INSERT INTO `problems` VALUES ('330', '按照实验控制的程度划分，教育实验可以分为（   ）。', 'A. 探索性实验 \nB. 真实验 \nC. 准实验 \nD. 前实验 \n', '正确答案是：前实验, 准实验, 真实验');
INSERT INTO `problems` VALUES ('331', '文献综述的撰写，一般包括（ ）。', 'A. 总结 \nB. 主体 \nC. 参考文献 \nD. 前言 \n', '正确答案是：前言, 主体, 总结, 参考文献');
INSERT INTO `problems` VALUES ('332', '下列属于教育叙事研究常用方法的是（  ）。', 'A. 实验法 \nB. 个案研究法 \nC. 观察法 \nD. 访谈法 \n', '正确答案是：观察法, 访谈法, 个案研究法');
INSERT INTO `problems` VALUES ('333', '教育研究的一般原则包括（   ）。', 'A. 创新性原则 \nB. 伦理原则 \nC. 定性与定量相结合原则 \nD. 客观性原则 \nE. 理论联系实际原则 \n', '正确答案是：客观性原则, 创新性原则, 理论联系实际原则, 定性与定量相结合原则, 伦理原则');
INSERT INTO `problems` VALUES ('334', '撰写教育研究报告应注意的问题有(   )。 ', 'A. 讨论分析要用辩证观点，不带个人色彩 \nB. 实事求是 \nC. 观点和资料相结合 \nD. 重点应放在研究方法和结果方面 \n', '正确答案是：实事求是, 观点和资料相结合, 重点应放在研究方法和结果方面, 讨论分析要用辩证观点，不带个人色彩');
INSERT INTO `problems` VALUES ('335', '在教育研究中常用的收集数据工具的是（ ）。', 'A. 观察 \nB. 访谈 \nC. 实物分析 \nD. 定量研究 \n', '正确答案是：观察, 访谈, 实物分析');
INSERT INTO `problems` VALUES ('336', ' 问卷调查的特点表现在（  ）。', 'A. 定量性 \nB. 统一性 \nC. 广泛性 \nD. 高效性 \n', '正确答案是：高效性, 统一性, 广泛性, 定量性');
INSERT INTO `problems` VALUES ('337', '下列参考文献的书写格式中，正确的是(     )。', 'A. 大前研一著,裴立杰译:《专业主义》，中信出版社2006年版.48页. \nB. 王海英.20世纪中国儿童观研究的反思[J].教育研究与实验.2008,(6)：34. \nC. 王国良.《明清时期儒学核心价值的转变》[M].安徽：安徽大学出版社，2002.15. \nD. 王天一.外国教育史[C].北京：北京师范大学出版社，1993.52.  \n', '正确答案是：王海英.20世纪中国儿童观研究的反思[J].教育研究与实验.2008,(6)：34.');
INSERT INTO `problems` VALUES ('338', '有教师在观察学生学习时，把认真做笔记的学生用“▲”表示，不认真做笔记的学生用“△”表示。此观察运用了哪种记录法（  ）。', 'A. 等级记录法 \nB. 描述记录法 \nC. 标记记录法 \nD. 连续记录法 \n', '正确答案是：标记记录法');
INSERT INTO `problems` VALUES ('339', '教育观察具有的功能（ ）。', 'A. 教育观察是检验教育理论是否正确的重要途径 \nB. 教育观察无需耗费很多时间 \nC. 能够观察到被研究者的内心活动 \nD. 能够观察过去和未来的现象 \n', '正确答案是：教育观察是检验教育理论是否正确的重要途径');
INSERT INTO `problems` VALUES ('340', '观察者融入被观察者群体中，参与他们的日常生活，以活动成员的角色开展研究活动，这属于（  ）。', 'A. 非参与式观察 \nB. 非结构式观察 \nC. 结构式观察 \nD. 参与式观察 \n', '正确答案是：参与式观察');
INSERT INTO `problems` VALUES ('341', '在一项关于小学一年级识字教学的行动研究中，李老师拟通过玩游戏、猜字谜、运用拆字法、编顺口溜等方法开展识字教学，这是行动研究中的哪一环节（  ）。', 'A. 提出问题 \nB. 计划  \nC. 行动 \nD. 反思 \n', '正确答案是：计划 ');
INSERT INTO `problems` VALUES ('342', '如下关于教育行动研究的理解，不正确的是（  ）。  ', 'A. 行动研究者在实验室情境中开展研究 \nB. 强调用科学的方法对自己的行动进行研究 \nC. 行动研究者在自身所处的教育情境中开展研究 \nD. 行动研究者对自己的实践进行批判性思考 \n', '正确答案是：行动研究者在实验室情境中开展研究');
INSERT INTO `problems` VALUES ('343', '根据林天佑教授的分类，在教育研究领域中，如果以理论与实践分类，教育行动研究属于（  ）。', 'A. 其它三个选项都不对 \nB. 探究实践 \nC. 探究理论 \nD. 探究理论与实践的关系   \n', '正确答案是：探究实践');
INSERT INTO `problems` VALUES ('344', '在“不同教育水平的师生课堂交流研究”的陈述中，其中因变量是（  ）', 'A. 小学 \nB. 初中 \nC. 高中 \nD. 测量出的师生课堂交流各项指标的分数 \n', '正确答案是：测量出的师生课堂交流各项指标的分数');
INSERT INTO `problems` VALUES ('345', '“教师情绪对教学效果影响”的实验研究中，“教师情绪”是（  ）', 'A. 因变量 \nB. 其它三个选项都不对 \nC. 无关变量 \nD. 自变量 \n', '正确答案是：自变量');
INSERT INTO `problems` VALUES ('346', '针对单一对象采用各种方法收集完整的资料进行细致深入研究的方法叫做（  ）。', 'A. 抽样调查 \nB. 现状调查 \nC. 个案调查  \nD. 普遍调查 \n', '正确答案是：个案调查 ');
INSERT INTO `problems` VALUES ('347', '教育研究的结果运用到实际情景中指导实践是一个需要反复验证的过程，其产生的效果往往不是立竿见影的，需要一个积累和检验的过程，这体现出教育研究的（  ）。', 'A. 实践性 \nB. 包容性 \nC. 后效性 \nD. 整合性 \n', '正确答案是：后效性');
INSERT INTO `problems` VALUES ('348', '比较研究法按目标的指向，可以分为（  ）', 'A. 求同比较和求异比较 \nB. 定性比较和定量比较 \nC. 单向比较和综合比较 \nD. 横向比较和纵向比较 \n', '正确答案是：求同比较和求异比较');
INSERT INTO `problems` VALUES ('349', '在教育实验设计中，不能随机选择和安排被试，但尽可能地控制无关变量，有效操控实验变量的的实验是（   ）。', 'A. 真实验 \nB. 探索性实验 \nC. 前实验 \nD. 准实验 \n', '正确答案是：准实验');
INSERT INTO `problems` VALUES ('350', '问题应简明准确，表达应避免语法错误，多用中性词，这体现出问卷设计的（  ）原则。', 'A. 采用投射性问题 \nB. 措辞规范 \nC. 内容简单详尽 \nD. 概念明确清晰 \n', '正确答案是：措辞规范');
INSERT INTO `problems` VALUES ('351', '表述教育行动研究结果时，被研究者的姓名没有按要求化名，该做法违背了（  ）。', 'A. 研究目的 \nB. 研究伦理 \nC. 研究规范 \nD. 研究者的意愿 \n', '正确答案是：研究伦理');
INSERT INTO `problems` VALUES ('352', '访谈中为建立融洽的访谈氛围，研究者可以（  ）。', 'A. 自我介绍 \nB.  联络感情 \nC. 从对方熟悉的事情谈起  \nD. 说明来意 \n', '正确答案是：从对方熟悉的事情谈起 ,  联络感情');
INSERT INTO `problems` VALUES ('353', '在实施教育观察时，一般包括哪些基本环节（ ）。', 'A. 得出研究结论 \nB. 界定研究问题 \nC. 整理分析记录资料 \nD. 制定观察计划、开展观察与记录 \n', '正确答案是：界定研究问题, 制定观察计划、开展观察与记录, 整理分析记录资料, 得出研究结论');
INSERT INTO `problems` VALUES ('354', '在“教材内容影响学生学习成绩”的实验中，属于无关变量的是（   ）。', 'A. 学生的智力水平 \nB. 教师的教学能力 \nC. 教学方法 \nD. 教材内容 \n', '正确答案是：教学方法, 教师的教学能力, 学生的智力水平');
INSERT INTO `problems` VALUES ('355', '下列参考文献的书写格式，正确的是(   )。', 'A. 水利局.水资源管理信息技术报告[R].北京:人民教育出版社，2010. \nB. 张广利.《儿童文学研究》[D].华东师范大学,2003. \nC. 刘军宁.人的发展[A]//俞可平.治理与善治[C].北京:社会科学文献出版社,2000. \nD. 许杰.教育分权[J].中国教育报,2008-11-14. \n', '正确答案是：水利局.水资源管理信息技术报告[R].北京:人民教育出版社，2010., 刘军宁.人的发展[A]//俞可平.治理与善治[C].北京:社会科学文献出版社,2000.');
INSERT INTO `problems` VALUES ('356', '选择合适的测量工具主要从（  ）方面进行考虑。', 'A. 测量工具的实用性 \nB. 测量工具的操作性 \nC. 测量工具的性能 \nD. 测量工具的价值 \n', '正确答案是：测量工具的性能, 测量工具的价值');
INSERT INTO `problems` VALUES ('357', '几种常见的课堂观察记录工具有（ ）。', 'A. 综合式 \nB. 流程式 \nC. 编码式 \nD. 划记式 \n', '正确答案是：综合式, 流程式, 编码式, 划记式');
INSERT INTO `problems` VALUES ('358', '教育行动研究中，“进一步研究构想”包括的内容有（  ）。', 'A. 实施情况 \nB. 修正构想 \nC. 反思建议 \nD. 拓展或加深研究 \n', '正确答案是：修正构想, 拓展或加深研究');
INSERT INTO `problems` VALUES ('359', '教育行动研究重视协同合作，合作类型包括（  ）。', 'A. 高校与研究机构之间的合作 \nB. 一线教师之间的合作 \nC. 高校、研究机构的专家与一线教师的合作 \nD. 高校专家之间的合作 \n', '正确答案是：高校、研究机构的专家与一线教师的合作, 一线教师之间的合作');
INSERT INTO `problems` VALUES ('360', '根据实验中所研究的实验变量数目的多少，可将教育实验分为（   ）。', 'A. 真实验 \nB. 单因素实验    \nC. 多因素实验 \nD. 准实验 \n', '正确答案是：单因素实验   , 多因素实验');
INSERT INTO `problems` VALUES ('361', '下列属于检索性文献的是（  ）。', 'A. 数据手册 \nB. 书目 \nC.  题录 \nD.  文摘 \n', '正确答案是： 题录,  文摘, 书目');
INSERT INTO `problems` VALUES ('362', '下列关于叙事研究的特点说法，正确的是（ ）。', 'A. 教育叙事要内含教育意义 \nB. 教育叙事要对过程中的细节加以关注 \nC. 叙述的事例是真实的、已经发生的 \nD. 教育叙事只能是自叙 \n', '正确答案是：叙述的事例是真实的、已经发生的, 教育叙事要内含教育意义, 教育叙事要对过程中的细节加以关注');
INSERT INTO `problems` VALUES ('363', '教育研究的意义有（   ）。', 'A. 教育研究能力是现代教育工作者的必备素质 \nB. 教育研究是教育实现自身变革的内在要求 \nC. 教育研究是提高教育行政决策的科学化的需要 \nD. 教育研究有益于中小学教师工作内驱力的激发 \nE. 教育研究有助于教育实践问题的解决 \n', '正确答案是：教育研究是教育实现自身变革的内在要求, 教育研究是提高教育行政决策的科学化的需要, 教育研究能力是现代教育工作者的必备素质, 教育研究有益于中小学教师工作内驱力的激发, 教育研究有助于教育实践问题的解决');
INSERT INTO `problems` VALUES ('364', '下列关于教育课题研究报告撰写的说法，不正确的是。 ', 'A. 不论是哪种类型的研究报告，都必须撰写研究结论。 \nB. 研究发现中最重要的是研究设计。  \nC. 任何研究报告的标题都会冠以适当的、能反映研究主题的、简练的标题。 \nD. 写作过程中的注释文献统一放到附录中。 \n', '正确答案是：研究发现中最重要的是研究设计。 , 写作过程中的注释文献统一放到附录中。');
INSERT INTO `problems` VALUES ('365', '真实验设计的特点是（  ）。', 'A. 严格控制无关变量 \nB. 有效操控实验变量 \nC. 不控制无关变量 \nD. 被试是随机的 \n', '正确答案是：被试是随机的, 严格控制无关变量, 有效操控实验变量');
INSERT INTO `problems` VALUES ('366', '正式的问卷在内容上应该包括（  ）。', 'A. 标题 \nB. 指导语 \nC. 问题 \nD. 调查者的信息 \n', '正确答案是：标题, 指导语, 问题');
INSERT INTO `problems` VALUES ('367', '下列参考文献的书写格式中，错误的是（  ）。', 'A. 钟秉林.应对高考招生制度改革新挑战[J].教育研究,2015,(3). \nB. 谢小庆.考试观念的变革[D].广东:华南师范大学,2013. \nC. 丁文祥.数字革命与竞争国际化[N].中国青年报，2000-11-20（15）. \nD. 白书农.植物开花研究[A].李承森.植物科学进展[C].北京：高等教育出版社，1998. \n', '正确答案是：谢小庆.考试观念的变革[D].广东:华南师范大学,2013.');
INSERT INTO `problems` VALUES ('368', '在教育观察记录方法的分类中，对自然发生的顺序事件或行为在一定时间内作连续不断记录的方法是（  ）。', 'A. 时间抽样 \nB. 连续记录法 \nC. 轶事记录法 \nD. 日记描述法 \n', '正确答案是：连续记录法');
INSERT INTO `problems` VALUES ('369', '教育观察，根据不同的标准，可以分为不同的类型。自然情境观察和实验室观察是根据什么标准划分的（ ）。', 'A. 观察情境的性质 \nB. 观察者是否直接介入观察对象的活动 \nC. 观察内容是否有统一设计、明确具体的观察要求 \nD. 观察时是否借助有关仪器设备 \n', '正确答案是：观察情境的性质');
INSERT INTO `problems` VALUES ('370', '如下研究题目中，适合用教育行动研究开展的研究题目是（  ）。', 'A. 学生身心发展的关键期研究 \nB. 提升初中生写作能力的策略研究 \nC. 教育核心价值研究  \nD. 教育家办学研究 \n', '正确答案是：提升初中生写作能力的策略研究');
INSERT INTO `problems` VALUES ('371', '在教育实验研究中，因变量又称（ ）', 'A. 实验变量 \nB. 结果变量 \nC. 原因变量 \nD. 操控变量 \n', '正确答案是：结果变量');
INSERT INTO `problems` VALUES ('372', '对所有的研究对象都加以调查.对调查对象所包含的每个部分进行逐个调查的是（    ）。', 'A. 现状调查 \nB. 抽样调查 \nC. 个案调查 \nD. 普遍调查  \n', '正确答案是：普遍调查 ');
INSERT INTO `problems` VALUES ('373', '“你打算辞职另找一份工作吗？”调查问卷中的这一题目违背了问卷题目设计的哪一原则（  ）', 'A. 避免一题多问 \nB. 概念明确清晰 \nC. 避免诱导性 \nD. 内容简单而详尽 \n', '正确答案是：避免一题多问');
INSERT INTO `problems` VALUES ('374', '重复性访谈又称为（  ）。', 'A. 多次性访谈 \nB. 焦点访谈 \nC. 纵向访谈 \nD. 横向访谈 \n', '正确答案是：纵向访谈, 多次性访谈');
INSERT INTO `problems` VALUES ('375', '教育观察具有的显著特点是（ ）。', 'A. 观察记录的翔实性 \nB. 目的性 \nC. 客观性 \nD. 计划性 \n', '正确答案是：目的性, 计划性, 客观性, 观察记录的翔实性');
INSERT INTO `problems` VALUES ('376', '在研究“自主探究教学方法对学生学习成绩的影响”的实验中，下列表述正确的是（  ）。', 'A. 学习成绩是自变量 \nB. 学习成绩是因变量 \nC. 教学方法是自变量 \nD. 教学方法是因变量 \n', '正确答案是：教学方法是自变量, 学习成绩是因变量');
INSERT INTO `problems` VALUES ('377', '下列选项中，符合选题原则的是（ ）。', 'A. 可行性原则 \nB. 创新性原则 \nC. 需要性原则 \nD. 科学性原则 \n', '正确答案是：需要性原则, 科学性原则, 创新性原则, 可行性原则');
INSERT INTO `problems` VALUES ('378', '在教师行动研究中，探讨文献的主要目的在于（  ）。', 'A. 确认研究问题的内容 \nB. 分析问题形成的可能原因 \nC. 了解研究问题的现状 \nD. 了解解决问题的可能做法 \n', '正确答案是：确认研究问题的内容, 分析问题形成的可能原因, 了解解决问题的可能做法');
INSERT INTO `problems` VALUES ('379', '教育行动研究本质上是为了追求一种更为合理的教育教学实践过程，旨在使教师（ ）。', 'A. 构建关于课堂教学的新理论 \nB. 获得一种内在启蒙和解放的力量 \nC. 增强实践能力与自我的超越能力 \nD. 打开新的思考角度与新的探寻方向 \n', '正确答案是：获得一种内在启蒙和解放的力量, 打开新的思考角度与新的探寻方向, 增强实践能力与自我的超越能力');
INSERT INTO `problems` VALUES ('380', '根据实验场所的不同，可将教育实验分为（  ）。', 'A. 真实验 \nB. 自然实验 \nC. 准实验 \nD. 实验室实验 \n', '正确答案是：实验室实验, 自然实验');
INSERT INTO `problems` VALUES ('381', '教育调查报告的主体部分的写作，常见的结构表现形式有(   )。 ', 'A. 纵横直线结构 \nB. 纵横交错结构 \nC. 横式结构 \nD. 纵式结构 \n', '正确答案是：纵式结构, 横式结构, 纵横交错结构');
INSERT INTO `problems` VALUES ('382', '下列方法中哪些属于实证方法（   ）。', 'A. 访谈 \nB. 测量 \nC. 问卷 \nD. 观察 \nE. 归纳 \n', '正确答案是：观察, 问卷, 访谈, 测量');
INSERT INTO `problems` VALUES ('383', '关键词是教育研究学术论文的构成要素之一，以下关于关键词的说法，正确的是(   )。', 'A. 每篇论文关键词的数量一般要3-5个 \nB. 关键词要尽量反映论文的主题概念 \nC. 关键词可在标题中选择，也可在论文中选择 \nD. 关键词是反映论文主要内容的名词性术语 \n', '正确答案是：关键词要尽量反映论文的主题概念, 每篇论文关键词的数量一般要3-5个, 关键词可在标题中选择，也可在论文中选择, 关键词是反映论文主要内容的名词性术语');
INSERT INTO `problems` VALUES ('384', '对待研究对象的伦理主要包括（ ）。', 'A. 避免身心伤害 \nB. 信守承诺 \nC. 尊重自由抉择的权利 \nD. 开诚布公 \n', '正确答案是：开诚布公, 尊重自由抉择的权利, 信守承诺, 避免身心伤害');
INSERT INTO `problems` VALUES ('385', '在问卷设计中，结构式的问题又被称为（  ）。', 'A. 问答式问题 \nB. 标准问题 \nC. 封闭式问题 \nD. 选择式问题 \n', '正确答案是：封闭式问题, 选择式问题');
INSERT INTO `problems` VALUES ('386', '下列参考文献的书写格式中，正确的是（    ）', 'A. 义务教育语文课程标准（2011年版）[S].北京:北京师范大学出版社,2012:17. \nB. 中华人民共和国教育部.义务教育语文课程标准（2011年版）[D].北京:北京师范大学出版社,2012:17. \nC. 义务教育语文课程标准（2011年版）[M].中华人民共和国教育部.北京:北京师范大学出版社,2012:17. \nD. 中华人民共和国教育部.义务教育语文课程标准（2011年版）[S].北京:北京师范大学出版社,2012:17. \n', '正确答案是：中华人民共和国教育部.义务教育语文课程标准（2011年版）[S].北京:北京师范大学出版社,2012:17.');
INSERT INTO `problems` VALUES ('387', '为了记录小学生课堂学习的情绪状态，情绪愉悦的学生用“√”表示，情绪不愉悦的学生用“×”表示，这种记录法属于（    ）', 'A. 标记记录法 \nB. 等级记录法 \nC. 连续记录法 \nD. 描述记录法  \n', '正确答案是：标记记录法');
INSERT INTO `problems` VALUES ('388', '根据观察内容是否有统一设计、明确具体的观察要求，教育观察可分为（ ）。', 'A. 自然情境观察和实验室观察 \nB. 参与性观察和非参与性观察 \nC. 结构式观察与非结构式观察 \nD. 直接观察和间接观察 \n', '正确答案是：结构式观察与非结构式观察');
INSERT INTO `problems` VALUES ('389', '“锦绣中文计划”的教育行动研究中，按照“计划、实践、观察、反思、下一步行动建议”五阶段进行教育行动研究，其中，“从综合高效识字教材中选取能配合教科书单元主题的材料，学习与主题有关的字词，共选出七篇综合高效识字的材料”属于哪一阶段的描述（  ）。', 'A. 实践  \nB. 观察 \nC. 反思 \nD. 计划 \n', '正确答案是：实践 ');
INSERT INTO `problems` VALUES ('390', '“你最近是否准备出国深造？”调查问卷中的这一题目违背了问卷题目设计的哪一原则（  ）', 'A. 概念明确清晰 \nB. 内容简单而详尽 \nC. 避免诱导性 \nD. 避免一题多问 \n', '正确答案是：概念明确清晰');
INSERT INTO `problems` VALUES ('391', '以人员接触情况划分，访谈可以分为（  ）。', 'A. 面对面访谈 \nB. 个人访谈 \nC. 网上访谈 \nD. 电话访谈 \n', '正确答案是：面对面访谈, 电话访谈, 网上访谈');
INSERT INTO `problems` VALUES ('392', '根据观察时是否借助有关仪器设备，教育观察可分为（ ）。', 'A. 直接观察 \nB. 间接观察 \nC. 实验室观察 \nD. 自然情境观察 \n', '正确答案是：直接观察, 间接观察');
INSERT INTO `problems` VALUES ('393', '进入21世纪，教育行动研究呈现出多元发展的格局，具体表现为（  ）。', 'A. 教育行动研究与教师专业发展 \nB. 教育行动研究与课堂教学实践 \nC. 校本行动研究与学校管理 \nD. 课程行动研究 \n', '正确答案是：课程行动研究, 教育行动研究与教师专业发展, 教育行动研究与课堂教学实践, 校本行动研究与学校管理');
INSERT INTO `problems` VALUES ('394', '按照假设在陈述变量关系的倾向性分，可以把假设分为（    ）。', 'A. 非定向假设 \nB. 特定假设 \nC. 虚无假设 \nD. 定向假设 \nE. 一般假设 \n', '正确答案是：特定假设, 定向假设, 一般假设, 虚无假设, 非定向假设');
INSERT INTO `problems` VALUES ('395', '下列关于教育实验研究的表述正确的是（   ）。', 'A. 教育实验研究需要对实验变量进行操控 \nB. 教育实验研究需要合理控制无关变量 \nC. 教育实验研究的目的在于探讨变量之间的因果关系 \nD. 控制、操纵、测量是教育实验研究的基本特点 \n', '正确答案是：控制、操纵、测量是教育实验研究的基本特点, 教育实验研究需要对实验变量进行操控, 教育实验研究需要合理控制无关变量, 教育实验研究的目的在于探讨变量之间的因果关系');
INSERT INTO `problems` VALUES ('396', '下列关于文献综述的撰写，表述正确的是（ ）。', 'A. 撰写文献综述，需要保持每段呈现一个作者的观点，列清楚 \nB. 撰写文献综述，可以在叙述完问题后，发表自己的看法 \nC. 撰写文献综述，参考文献是不可以省略的  \nD. 撰写文献综述，主要根据自己的能力适当向前延伸文献 \n', '正确答案是：撰写文献综述，可以在叙述完问题后，发表自己的看法, 撰写文献综述，参考文献是不可以省略的 ');
INSERT INTO `problems` VALUES ('397', '按照研究问题的性质，教育研究可以分为（   ）。', 'A. 现实研究 \nB. 超前预测研究 \nC. 历史研究 \nD. 教育价值研究 \nE. 教育事实研究 \n', '正确答案是：教育价值研究, 教育事实研究');
INSERT INTO `problems` VALUES ('398', '教育研究成果表述中，需要注意的基本要求是（ ）。', 'A. 研究立论从材料出发，不得主观臆造 \nB. 文字表达要求规范、精炼、简洁，表达准确完整 \nC. 内容表述要求观点和材料一致 \nD. 文献借鉴要求适度和规范 \n', '正确答案是：内容表述要求观点和材料一致, 研究立论从材料出发，不得主观臆造, 文献借鉴要求适度和规范, 文字表达要求规范、精炼、简洁，表达准确完整');
INSERT INTO `problems` VALUES ('399', '判断一篇论文是否有意义，主要看（  ）。', 'A. 记录内容是不是多样 \nB. 在实践中能不能应用 \nC. 在表达上是否流畅 \nD. 在理论上是否站得住脚 \n', '正确答案是：在理论上是否站得住脚, 在实践中能不能应用');
INSERT INTO `problems` VALUES ('400', '记录学生在某一活动中的表现时，有观察者用“5”表示十分活跃，“4”表示“活跃”，“3”表示“一般”，“2”表示“不活跃”，“1”表示“很不活跃”，此记录方法是（ ）。', 'A. 连续记录法 \nB. 标记记录法 \nC. 描述记录法 \nD. 等级记录法 \n', '正确答案是：等级记录法');
INSERT INTO `problems` VALUES ('401', '一名高中教师在开展教学的过程中，对其学生所进行的观察属于（  ）。', 'A. 参与性观察 \nB. 非参与性观察 \nC. 结构式观察 \nD. 间接观察 \n', '正确答案是：参与性观察');
INSERT INTO `problems` VALUES ('402', '“幼儿园班上的学生不喜欢吃午餐”、“学生社会领域学习成效不如预期”、“对于现有教材不满”等是教育行动研究步骤中哪一步骤的表述：（  ）。', 'A. 研究设计与实施 \nB. 说明研究动机与目的 \nC. 研究发现与反思 \nD. 确定研究题目 \n', '正确答案是：说明研究动机与目的');
INSERT INTO `problems` VALUES ('403', '“大多数教师都认为上课聊天会导致注意力不集中，你同意吗？”调查问卷中的这一题目违背了问卷题目设计的哪一原则（  ）。', 'A. 内容简单而详尽 \nB. 概念明确清晰 \nC. 避免一题多问 \nD. 避免诱导性 \n', '正确答案是：避免诱导性');
INSERT INTO `problems` VALUES ('404', '教育行动研究的“研究设计与实施”包含的内容之一是研究架构，关于研究架构，表述正确的是（  ）。', 'A. 研究架构要包括修正途径以及进一步研究 \nB. 把行动的要素与流程用图形显示出来 \nC. 研究架构是把行动的要素用图表显示出来 \nD. 研究架构要能充分显示行动的内容与过程 \n', '正确答案是：把行动的要素与流程用图形显示出来, 研究架构要能充分显示行动的内容与过程, 研究架构要包括修正途径以及进一步研究');
INSERT INTO `problems` VALUES ('405', '影响教育实验外在效度的主要因素是（  ）。', 'A. 测量工具的局限性  \nB. 初试样本缺乏代表性 \nC. 实验情境的人为性 \nD. 被试效应与实验者效应 \n', '正确答案是：实验情境的人为性, 初试样本缺乏代表性, 测量工具的局限性 ');
INSERT INTO `problems` VALUES ('406', '教育文献检索常用的检索工具是(   )。', 'A. 数据 \nB. 书目 \nC. 文摘 \nD. 索引 \n', '正确答案是：书目, 索引, 文摘');
INSERT INTO `problems` VALUES ('407', '教育研究成果表述的基本形式，包括（ ）。', 'A. 观察记录 \nB. 专著 \nC. 学术论文 \nD. 研究报告 \n', '正确答案是：研究报告, 学术论文, 专著');
INSERT INTO `problems` VALUES ('408', '用语言文字对所观察的事件做详细记录，但常无法进行分类，且大多只适用于个别人或事的观察记录方法是（ ）。', 'A. 描述记录法 \nB. 标记记录法 \nC. 事件抽样 \nD. 等级记录法 \n', '正确答案是：描述记录法');
INSERT INTO `problems` VALUES ('409', '能对观察资料进行定量分析和对比研究的是（    ）', 'A. 非结构式观察 \nB. 结构式观察 \nC. 实验室观察 \nD. 自然情景观察 \n', '正确答案是：结构式观察');
INSERT INTO `problems` VALUES ('410', '问卷中的问题应明确具体，不可模棱两可指的是（  ）原则。', 'A. 概念明确清晰 \nB. 措辞规范 \nC. 采用投射性问题 \nD. 内容详细而简单 \n', '正确答案是：概念明确清晰');
INSERT INTO `problems` VALUES ('411', '教师行动研究的“研究设计与实施”步骤大致包括哪些内容（  ）。', 'A. 研究架构 \nB. 研究过程 \nC. 研究情境 \nD. 资料分析 \n', '正确答案是：研究架构, 研究情境, 研究过程, 资料分析');
INSERT INTO `problems` VALUES ('412', '以下不属于教育实验外在效度影响因素的是（   ）。', 'A. 发展与成熟 \nB. 测量工具的局限性  \nC. 历史或经历 \nD. 实验情境的人为性  \n', '正确答案是：发展与成熟, 历史或经历');
INSERT INTO `problems` VALUES ('413', '下列属于文献综述的主体部分的撰写的形式的是（  ）。', 'A. 按不同主题 \nB. 按年代顺序 \nC. 按不同观点  \nD. 按发展阶段 \n', '正确答案是：按年代顺序, 按发展阶段, 按不同主题, 按不同观点 ');
INSERT INTO `problems` VALUES ('414', '下列选项中不属于描述记录法的是（   ）', 'A. 连续记录法 \nB. 日记记录法 \nC. 轶事记录法 \nD. 标记记录法 \n', '正确答案是：标记记录法');
INSERT INTO `problems` VALUES ('415', '在设计一些可能由明显社会要求和规范影响的问卷项目时，要设法掩盖研究目的，应尽可能（  ）。', 'A. 措辞规范 \nB.  避免一题多问 \nC. 概念清晰明确 \nD. 采用透射式问题 \n', '正确答案是：采用透射式问题');
INSERT INTO `problems` VALUES ('416', '确定研究题目时，可以参考的标准有（  ）。', 'A. 研究题目不必很具体 \nB. 研究时间可以不设限 \nC. 研究要以学生权益为第一考虑 \nD. 以教学情境中遇到的问题为研究题目  \n', '正确答案是：以教学情境中遇到的问题为研究题目 , 研究要以学生权益为第一考虑');
INSERT INTO `problems` VALUES ('417', '下列属于教育调查报告前言的写法的是(   )。', 'A. 情况交代法 \nB. 提问设悬法 \nC. 结论先行法 \nD. 目的直述法 \n', '正确答案是：目的直述法, 提问设悬法, 情况交代法, 结论先行法');
INSERT INTO `problems` VALUES ('418', '为了研究儿童发展的规律，对儿童的成长做传记式记录，其运用的记录方法是（ ）。', 'A. 轶事记录法 \nB. 日记描述法 \nC. 标记记录法 \nD. 连续记录法 \n', '正确答案是：日记描述法');
INSERT INTO `problems` VALUES ('419', '“你喜欢看课外书吗”这一问题违反了问卷设计的（  ）原则。', 'A. 语言表达及项目设置上力避诱导性 \nB. 概念明确清晰 \nC.  内容简单详尽 \nD. 避免一题多问 \n', '正确答案是：语言表达及项目设置上力避诱导性');
INSERT INTO `problems` VALUES ('420', '“你是否认为用MOOC进行教学很重要?”调查问卷中的这一题目违背了问卷题目设计的哪一原则（  ）。', 'A. 避免一题多问 \nB. 避免术语和缩略语 \nC. 内容简单而详尽 \nD. 避免诱导性 \n', '正确答案是：避免术语和缩略语');
INSERT INTO `problems` VALUES ('421', '一般情况下，问卷的长度应控制在（  ）。', 'A. 41-50分钟 \nB. 31-40分钟 \nC. 16-30分钟 \nD. 15分钟之内 \n', '正确答案是：15分钟之内');
